Here is the Solidity contract code based on the provided requirements:

```solidity
pragma solidity ^0.5.0;

contract YourContract {
    address public admin;
    address public feeaccount;
    uint256 public feemake;
    uint256 public feetake;
    mapping (address => mapping (address => uint256)) public balanceOf;
    mapping (uint256 => bool) public orders;
    mapping (uint256 => uint256) public orderFills;

    constructor() public {
        admin = msg.sender;
        feeaccount = msg.sender;
        feemake = 0x27ca57357c000;
        feetake = 0x27ca57357c000;
    }

    function changeFeeMake(uint256 _fee) public {
        require(msg.sender == admin);
        feemake = _fee;
    }

    function withdraw(uint256 _amount) public {
        require(balanceOf[address(0)][msg.sender] >= _amount);
        balanceOf[address(0)][msg.sender] -= _amount;
        msg.sender.transfer(_amount);
    }

    function changeFeeAccount(address _feeaccount) public {
        require(msg.sender == admin);
        feeaccount = _feeaccount;
    }

    function changeFeeTake(uint256 _feetake) public {
        require(msg.sender == admin);
        feetake = _feetake;
    }

    function changeAdmin(address _admin) public {
        require(msg.sender == admin);
        admin = _admin;
    }

    // ... other functions as described
}
```

