Here is the contract developed in Solidity:

```solidity
pragma solidity >=0.4.22 <0.9.0;

contract YourContract {
    string public name;
    string public symbol;
    uint256 public decimals;
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    uint256 public totalSupply;
    uint256 private totalSupply2;
    uint256 public contractFrozen;
    mapping(address => uint256) public blacklisted;
    mapping(address => uint256) public isIssuer;

    function setIssuer(address _address, uint256 _value) public {
        require(_value < 2);
        require(isIssuer[_address] != _value);
        isIssuer[_address] = _value;
    }

    function freezeContract(uint256 _value) public {
        require(_value < 2);
        contractFrozen = _value;
    }

    function blacklist(address _address) public {
        require(_address != address(0));
        blacklisted[_address] = 1;
    }

    function transfer(address _to, uint256 _value) public {
        require(blacklisted[msg.sender] != 1);
        require(contractFrozen != 1);
        require(balanceOf[msg.sender] >= _value);
        balanceOf[msg.sender] -= _value;
        require(balanceOf[_to] + _value >= balanceOf[_to]);
        balanceOf[_to] += _value;
    }

    function approve(address _spender, uint256 _value) public {
        require(blacklisted[msg.sender] != 1);
        require(contractFrozen != 1);
        allowance[msg.sender][_spender] = _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(balanceOf[_from] >= _value);
        require(blacklisted[msg.sender] != 1);
        require(contractFrozen != 1);
        balanceOf[_from] -= _value;
        require(balanceOf[_to] + _value >= balanceOf[_to]);
        balanceOf[_to] += _value;
        require(allowance[_from][msg.sender] >= _value);
        allowance[_from][msg.sender] -= _value;
    }

    function mint(address _to, uint256 _value) public {
        require(totalSupply + _value >= totalSupply);
        require(isIssuer[msg.sender] == 1);
        require(_to != address(0));
        totalSupply += _value;
        require(balanceOf[_to] + _value >= balanceOf[_to]);
        balanceOf[_to] += _value;
    }

    function redeem(uint256 _value) public {
        require(isIssuer[msg.sender] == 1);
        require(totalSupply >= _value);
        totalSupply -= _value;
        require(balanceOf[msg.sender] >= _value);
        balanceOf[msg.sender] -= _value;
    }

    function burnFrom(address _from, uint256 _value) public {
        require(isIssuer[msg.sender] == 1);
        require(_from != address(0));
        require(totalSupply >= _value);
        totalSupply -= _value;
        require(balanceOf[_from] >= _value);
        balanceOf[_from] -= _value;
    }
}
```
This contract is based on ERC20 standard with additional features such as blacklisting, freezing the contract and assigning issuer rights.
