Below is the Solidity contract code based on the given descriptions:

```solidity
pragma solidity ^0.8.0;

contract YourContract {
    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    address public multisigwallet;
    bool public active;
    address public owner;
    uint256 public onetokeninwei;

    constructor() payable {
        active = true;
        onetokeninwei = 0x8bec19599db8;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function withdraw() public {
        require(msg.sender == owner);
        payable(multisigwallet).transfer(address(this).balance);
    }

    function finalize() public {
        require(msg.sender == owner);
        active = false;
    }

    function decreaseApproval(address _spender, uint256 _value) public {
        uint256 oldValue = allowance[msg.sender][_spender];
        if (_value > oldValue) {
            allowance[msg.sender][_spender] = 0;
        } else {
            allowance[msg.sender][_spender] = oldValue - _value;
        }
    }

    fallback() external payable {
        require(active && msg.value > 0 && onetokeninwei != 0 && 0x1743b34e18439b502000000 >= totalsupply + (msg.value * 0xde0b6b3a7640000) / onetokeninwei);
        uint256 amount = (msg.value * 0xde0b6b3a7640000) / onetokeninwei;
        balanceof[msg.sender] += amount;
        totalsupply += amount;
    }

    function burn(uint256 _value) public {
        require(_value > 0);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_to != address(0));
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }

    function setTokenPriceInWei(uint256 _value) public {
        require(msg.sender == owner);
        onetokeninwei = _value;
    }

    function increaseApproval(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] += _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0));
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function mintTokens(address _to, uint256 _value) public {
        require(msg.sender == owner && 0x1743b34e18439b502000000 >= totalsupply + _value * 0xde0b6b3a7640000);
        uint256 amount = _value * 0xde0b6b3a7640000;
        balanceof[_to] += amount;
        totalsupply += amount;
    }
}
```
Please note that this contract does not include the necessary safety checks for overflows and underflows. You might want to use a library such as OpenZeppelin's SafeMath to prevent these potential issues.
