Here is a Solidity smart contract that implements the described logic:

```solidity
pragma solidity ^0.5.16;

contract YourContract {
  uint256 public totalSupply;
  uint256 public preicostartdate;
  uint256 public preicoenddate;
  uint256 public icostartdate;
  uint256 public icoenddate;
  mapping (address => uint256) public balanceof;
  mapping (address => mapping (address => uint256)) public allowance;
  address public owner;
  address public wallet;
  uint256 public rate;
  uint256 public mintransactionamount;
  uint256 public raisedforether;
  uint256 private totalSupply2;
  uint256 private s_13;
  uint256 private s_14;
  uint256 public bonusessold;
  uint256 public tokenssold;
  uint256 public stage1;
  uint256 public stage2;
  uint256 public stage3;
  uint256 public stage4;

  constructor() public payable {
    raisedforether = 0;
    totalSupply2 = 0x28a857425466f80000000;
    s_13 = 0x84595161401484a000000;
    s_14 = 0x27b46536c66c8e3000000;
    bonusessold = 0;
    tokenssold = 0;
    stage1 = 0x32d26d12e980b6000000;
    stage2 = 0x4c3ba39c5e4111000000;
    stage3 = 0xcb49b44ba602d8000000;
    stage4 = 0x1402daf2a58aae1000000;
  }

  function approve(address _spender, uint256 _value) public returns (bool) {
    require((_value == 0) || (allowance[msg.sender][_spender] == 0));
    allowance[msg.sender][_spender] = _value;
    return true;
  }

  function setupICOPeriod(uint256 start, uint256 end) public {
    require(msg.sender == owner && start < end);
    icostartdate = start;
    icoenddate = end;
  }

  function burnPREICOTokens() public {
    require(totalSupply2 > tokenssold);
    balanceof[address(this)] -= (totalSupply2 - tokenssold);
  }

  function withdrawEther(uint256 amount) public {
    require(msg.sender == owner);
    msg.sender.transfer(amount);
  }

  function transferFrom(address _from, address _to, uint256 _value) public returns (bool) {
    require(now > icoenddate);
    balanceof[_to] += _value;
    balanceof[_from] -= _value;
    allowance[_from][msg.sender] -= _value;
    return true;
  }

  function burnICOTokens() public {
    require(msg.sender == owner);
    balanceof[address(this)] = 0;
  }

  function setupPREICOPeriod(uint256 start, uint256 end) public {
    require(msg.sender == owner && start < end);
    preicostartdate = start;
    preicoenddate = end;
  }

  function burnBonuses() public {
    require(s_14 > bonusessold);
    balanceof[address(this)] -= (s_14 - bonusessold);
  }

  function transfer(address _to, uint256 _value) public returns (bool) {
    require(now > icoenddate);
    balanceof[_to] += _value;
    balanceof[msg.sender] -= _value;
    return true;
  }

  function transferOwnership(address newOwner) public {
    require(newOwner != address(0));
    owner = newOwner;
  }

  function getBonus(uint256 _value) public {
    require(now >= preicostartdate && now <= preicoenddate && _value != 0 && now >= icostartdate && bonusessold + ((20 * _value) / 100) <= s_14);
    bonusessold += ((20 * _value) / 100);
  }
}
```

