```solidity
pragma solidity ^0.5.0;

contract ShrimpFarm {
    uint256 public eggs_to_hatch_1shrimp;
    uint256 private totalSupply;
    uint256 private totalSupply1;
    address public ceoaddress;
    bool public initialized = false;
    mapping(address => uint256) public hatcheryshrimp;
    mapping(address => uint256) public claimedeggs;
    mapping(address => uint256) public lasthatch;
    mapping(address => address) public referrals;
    uint256 public marketeggs;

    constructor() public payable {
        require(msg.value == 0, "Cannot send Ether with constructor");
        ceoaddress = msg.sender;
        eggs_to_hatch_1shrimp = 0x15180;
        totalSupply = 10000;
        totalSupply1 = 5000;
    }

    function seedMarket(uint256 _eggs) public payable {
        require(marketeggs == 0, "Market eggs already seeded");
        initialized = true;
        marketeggs = _eggs;
    }

    function hatchEggs(address _referrer) public {
        require(initialized, "Contract not initialized yet");
        if(referrals[msg.sender] == address(0) && _referrer != msg.sender){
            referrals[msg.sender] = _referrer;
        }
        uint256 eggsUsed = getMyEggs();
        uint256 newShrimp = SafeMath.div(eggsUsed, eggs_to_hatch_1shrimp);
        hatcheryshrimp[msg.sender] = SafeMath.add(hatcheryshrimp[msg.sender], newShrimp);
        claimedeggs[msg.sender] = 0;
        lasthatch[msg.sender] = now;

        //send referral eggs
        claimedeggs[referrals[msg.sender]] = SafeMath.add(claimedeggs[referrals[msg.sender]], SafeMath.div(eggsUsed, 5));

        //boost market to nerf shrimp hoarding
        marketeggs = SafeMath.add(marketeggs, SafeMath.div(eggsUsed, 10));
    }

    function sellEggs() public {
        require(initialized, "Contract not initialized yet");
        uint256 hasEggs = getMyEggs();
        uint256 eggValue = calculateEggSell(hasEggs);
        uint256 fee = devFee(eggValue);
        claimedeggs[msg.sender] = 0;
        lasthatch[msg.sender] = now;
        marketeggs = SafeMath.add(marketeggs, hasEggs);
        ceoaddress.transfer(fee);
        msg.sender.transfer(SafeMath.sub(eggValue, fee));
    }

    function getBalance() public view returns(uint256){
        return address(this).balance;
    }

    function getMyShrimp() public view returns(uint256){
        return hatcheryshrimp[msg.sender];
    }

    function getMyEggs() public view returns(uint256){
        return SafeMath.add(claimedeggs[msg.sender], getEggsSinceLastHatch(msg.sender));
    }

    function calculateTrade(uint256 _rt, uint256 _rs, uint256 _bs) public view returns(uint256){
        return SafeMath.div(SafeMath.mul(_rt, _bs), _rs);
    }

    function calculateEggSell(uint256 _eggs) public view returns(uint256){
        return calculateTrade(_eggs, marketeggs, address(this).balance);
    }

    function calculateEggBuy(uint256 _eth, uint256 _contractBalance) public view returns(uint256){
        return calculateTrade(_eth, _contractBalance, marketeggs);
    }

    function getEggsSinceLastHatch(address _customer) public view returns(uint256){
        uint256 secondsPassed = SafeMath.sub(now, lasthatch[_customer]);
        return SafeMath.mul(secondsPassed, hatcheryshrimp[_customer]);
    }

    function devFee(uint256 _amount) public pure returns(uint256){
        return SafeMath.div(SafeMath.mul(_amount, 4), 100);
    }
}
```

Please note that this is a simplified version of the contract as per your requirements. It uses the SafeMath library for arithmetic operations to prevent overflows and underflows. The contract also requires the deployment of the SafeMath library.
