Here is a Solidity contract that implements the described functionality:

```solidity
pragma solidity ^0.8.0;

contract MyContract {
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    uint256 public remaining;
    uint256 public ethrate;
    address public owner;
    uint256 public collectedamount;
    uint256 public icostatus;
    uint256 public tokenprice;
    address public benaddress;
    address public bkaddress;
    uint256 public allowtransfertoken;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => uint256) public frozenaccount;

    constructor() {
        totalsupply = 0x204fce5e3e25026110000000;
        owner = msg.sender;
        balanceof[owner] = totalsupply;
        name = "0";
        symbol = "0";
        decimals = 18;
        remaining = totalsupply;
        ethrate = 300;
        icostatus = 1;
        tokenprice = 10;
        benaddress = 0x57D1aED65eE1921CC7D2F3702C8A28E5Dd317913;
        bkaddress = 0xe254fc78C94d7A358F78323e56D9bbBc4C2f9993;
        allowtransfertoken = 0;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function changeIcoStatus(uint8 _newStatus) public {
        require(msg.sender == owner || msg.sender == bkaddress);
        icostatus = _newStatus;
    }

    function burn(uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
        return true;
    }

    function withdraw(uint256 amount) public {
        require(msg.sender == owner || msg.sender == bkaddress);
        payable(benaddress).transfer(amount);
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);
        allowance[_from][msg.sender] -= _value;
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function setTokenPrice(uint256 _newPrice) public {
        require(msg.sender == owner || msg.sender == bkaddress);
        tokenprice = _newPrice;
    }

    function setEthRate(uint256 _newRate) public {
        require(msg.sender == owner || msg.sender == bkaddress);
        ethrate = _newRate;
    }

    function burnFrom(address _from, uint256 _value) public returns (bool success) {
        require(balanceof[_from] >= _value && _value <= allowance[_from][msg.sender]);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        totalsupply -= _value;
        return true;
    }

    function mintToken(uint256 _value) public {
        require(msg.sender == owner || msg.sender == bkaddress);
        balanceof[owner] += _value * 0xde0b6b3a7640000;
        totalsupply += _value * 0xde0b6b3a7640000;
    }

    function setTransferStatus(uint256 _newStatus) public {
        require(msg.sender == owner || msg.sender == bkaddress);
        allowtransfertoken = _newStatus;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(frozenaccount[msg.sender] == 0 && balanceof[msg.sender] >= _value && balanceof[_to] + _value >= balanceof[_to] && _to != address(0));
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        (bool success, ) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,address,bytes)", msg.sender, _value, this, _extraData));
        require(success);
        return true;
    }

    function withdraw_all() public {
        require(msg.sender == owner || msg.sender == bkaddress);
        payable(benaddress).transfer(address(this).balance);
    }

    function transferOwnership(address _newOwner) public {
        require(msg.sender == owner || msg.sender == bkaddress);
        balanceof[owner] = 0;
        balanceof[_newOwner] = remaining;
        owner = _newOwner;
    }

    function sellOffline(address _buyer, uint256 _value) public {
        require(_value * 0xde0b6b3a7640000 <= remaining && remaining > 0);
        remaining -= _value * 0xde0b6b3a7640000;
        balanceof[owner] -= _value * 0xde0b6b3a7640000;
        balanceof[_buyer] += _value * 0xde0b6b3a7640000;
    }
}
```

Please note that this is a high-level outline of the contract. A real-world contract would include additional checks, logging, and error handling. It would also be thoroughly tested. This contract has not been tested or audited, so it should not be used in production without further work.
