```solidity
pragma solidity ^0.6.1;

contract TradingContract {
    mapping (address => uint256) private addressOfBountyMembers;
    uint160 public admin;
    address public feeaccount;
    address public accountlevelsaddr;
    uint256 public feemake;
    uint256 public feetake;
    uint256 public feerebate;
    mapping (address => mapping (address => uint256)) public balanceOf;
    mapping (address => mapping (bytes32 => uint256)) public orders;
    mapping (address => mapping (bytes32 => uint256)) public orderFills;

    function order(address tokenGet, uint256 amountGet, address tokenGive, uint256 amountGive, uint256 expires, uint256 nonce) external {
        bytes32 v0 = sha256(abi.encodePacked(address(this), tokenGet, amountGet, tokenGive, amountGive, expires, nonce));
        orders[msg.sender][v0] = 1;
    }

    function cancelOrder(address tokenGet, uint256 amountGet, address tokenGive, uint256 amountGive, uint256 expires, uint256 nonce, uint8 v, bytes32 r, bytes32 s) external {
        bytes32 v0 = sha256(abi.encodePacked(address(this), tokenGet, amountGet, tokenGive, amountGive, expires, nonce));
        if(orders[msg.sender][v0] != 0) {
            orderFills[msg.sender][v0] = amountGet;
        } else {
            address v1 = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", v0)), v, r, s);
            if(v1 == msg.sender) {
                orderFills[msg.sender][v0] = amountGet;
            }
        }
    }

    function changeFeeMake(uint256 feeMake_) external {
        require(msg.sender == admin && feeMake_ <= feemake);
        feemake = feeMake_;
    }

    function withdraw(uint256 amount) external {
        require(amount <= addressOfBountyMembers[msg.sender] && amount <= admin);
        admin -= amount;
        msg.sender.transfer(amount);
    }

    function changeFeeAccount(address feeAccount_) external {
        require(msg.sender == admin);
        feeaccount = feeAccount_;
    }

    function changeFeeRebate(uint256 feeRebate_) external {
        require(msg.sender == admin && feeRebate_ <= feetake && feeRebate_ >= feerebate);
        feerebate = feeRebate_;
    }

    function changeAdmin(address admin_) external {
        require(msg.sender == admin);
        admin = uint160(admin_);
    }

    function depositToken(address token, uint256 amount) external {
        require(token != address(0));
        require(Token(token).transferFrom(msg.sender, address(this), amount));
        balanceOf[token][msg.sender] += amount;
    }

    function changeFeeTake(uint256 feeTake_) external {
        require(msg.sender == admin && feeTake_ <= feetake && feeTake_ >= feerebate);
        feetake = feeTake_;
    }

    function changeAccountLevelsAddr(address accountLevelsAddr_) external {
        require(msg.sender == admin);
        accountlevelsaddr = accountLevelsAddr_;
    }

    function deposit() external payable {
        admin += uint160(msg.value);
    }

    function withdrawToken(address token, uint256 amount) external {
        require(token != address(0) && amount <= balanceOf[token][msg.sender]);
        balanceOf[token][msg.sender] -= amount;
        require(Token(token).transfer(msg.sender, amount));
    }
}

interface Token {
    function transferFrom(address from, address to, uint256 value) external returns (bool);
    function transfer(address to, uint256 value) external returns (bool);
}
```

Please note that this contract is simplified and does not include any error handling or events. Also, it assumes that the `Token` contract is compatible with the ERC20 standard and has `transferFrom` and `transfer` methods.
