Based on the descriptions provided, the Solidity contract code would look something like this:

```solidity
pragma solidity ^0.5.0;

contract MyContract {

    address public owner;
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowanceeliminate;
    mapping (address => mapping (address => uint256)) public allowancetransfer;
    uint256 private totalSupply2;
    uint256 private totalSupply3;
    uint256 private totalSupply4;
    uint256 public sellprice;
    uint256 public buyprice;

    function eliminate(uint256 _value) public {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
    }

    function eliminateFrom(address _from, uint256 _value) public {
        require(_value <= allowanceeliminate[_from][msg.sender] && balanceof[_from] >= _value);
        balanceof[_from] -= _value;
        allowanceeliminate[_from][msg.sender] -= _value;
        totalsupply -= _value;
    }

    function updatePrice() public {
        if (totalsupply != 0) {
            sellprice = (10**uint256(decimals) * totalSupply2 * totalSupply3) / totalsupply;
            buyprice = (10**uint256(decimals) * totalSupply4 * totalSupply3) / totalsupply;
        }
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= allowancetransfer[_from][msg.sender]);
        allowancetransfer[_from][msg.sender] -= _value;
        require(balanceof[_from] >= _value && balanceof[_to] + _value > balanceof[_to] && _to != address(0));
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
    }

    function approveEliminate(address _spender, uint256 _value) public {
        allowanceeliminate[msg.sender][_spender] = _value;
    }

    function approveTransfer(address _spender, uint256 _value) public {
        allowancetransfer[msg.sender][_spender] = _value;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner);
        owner = newOwner;
    }

    function buy() public payable {
        require(buyprice != 0 && msg.sender != address(0) && balanceof[msg.sender] + ((1000 * msg.value) / buyprice) > balanceof[msg.sender]);
        balanceof[address(this)] -= ((1000 * msg.value) / buyprice);
        balanceof[msg.sender] += ((1000 * msg.value) / buyprice);
    }

    function transfer(address _to, uint256 _value) public {
        require(balanceof[_to] + _value > balanceof[_to] && _to != address(0) && balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function sell(uint256 _value) public {
        require(balanceof[address(this)] >= (_value * sellprice) / 1000 && balanceof[address(this)] + _value > balanceof[address(this)] && balanceof[msg.sender] >= _value && address(this) != address(0));
        balanceof[msg.sender] -= _value;
        balanceof[address(this)] += _value;
        msg.sender.transfer((_value * sellprice) / 1000);
    }
}
```
Note: This is a very basic and simplified contract. In real-world scenarios, you would want to add more checks and functionality to deal with potential errors and exceptions. Also, you would need to add events to log actions taken in the contract.
