```solidity
pragma solidity 0.6.12;

contract EtherContract {
    mapping(address => uint256) public userdepositedwei;
    mapping(address => uint256) public userwithdrewwei;
    mapping(address => uint256) public userreferralswei;
    mapping(address => uint256) public userdividendswei;
    uint256 public totaldepositedwei;
    uint256 public totalwithdrewwei;
    address payable constant targetAddress = 0x7DbBD1640A99AD6e7b08660C0D89C55Ec93E0896;

    constructor() public payable {
        totaldepositedwei = 0;
        totalwithdrewwei = 0;
    }

    fallback() external payable {
        require(msg.sender != address(0), "Invalid sender address");
        uint256 amount = msg.value;
        if(amount == 0) {
            (bool success,) = targetAddress.call{value: 0}("");
            require(success, "Transfer failed");
            userdividendswei[msg.sender] = block.number;
            userdepositedwei[msg.sender] += amount;
            totaldepositedwei += amount;
            if(userdepositedwei[msg.sender] == 0) {
                (bool success,) = msg.sender.call{value: 0}("");
                require(success, "Transfer failed");
                userwithdrewwei[msg.sender] += amount;
                totalwithdrewwei += amount;
            } else {
                uint256 dividend = ((userdepositedwei[msg.sender]*4)/100)*(block.number - userdividendswei[msg.sender])/5900;
                (bool success,) = msg.sender.call{value: dividend}("");
                if(!success) {
                    userwithdrewwei[msg.sender] += dividend;
                    totalwithdrewwei += dividend;
                }
            }
        }
        else {
            uint256 transferAmount = (amount*15)/100;
            (bool success,) = targetAddress.call{value: transferAmount}("");
            require(success, "Transfer failed");
            userdividendswei[msg.sender] = block.number;
            userdepositedwei[msg.sender] += amount;
            totaldepositedwei += amount;
            if(userdepositedwei[msg.sender] == 0) {
                (bool success,) = msg.sender.call{value: 0}("");
                require(success, "Transfer failed");
                userwithdrewwei[msg.sender] += amount;
                totalwithdrewwei += amount;
            } else {
                uint256 dividend = ((userdepositedwei[msg.sender]*4)/100)*(block.number - userdividendswei[msg.sender])/5900;
                (bool success,) = msg.sender.call{value: dividend}("");
                if(!success) {
                    userwithdrewwei[msg.sender] += dividend;
                    totalwithdrewwei += dividend;
                }
            }
        }
    }
}
```
This is a simple contract with a payable constructor and a fallback function. The contract keeps track of the total deposited and withdrew Wei, as well as individual user's deposited Wei, withdrew Wei, referrals Wei, and dividends Wei. The fallback function performs different actions based on the received msg.value and the user's deposited Wei.
