Here is the Solidity contract based on your description:

```Solidity
pragma solidity ^0.5.0;

contract Investment {
    mapping(address => uint256) public invested;
    mapping(address => uint256) public atblock;
    uint256 public previousbalance;
    uint256 public calculatedlow;
    uint256 public investedtotal;
    uint256 public interestrate;
    uint256 public nextblock;

    constructor() payable public {
        previousbalance = 0;
        calculatedlow = 0;
        investedtotal = 0;
        interestrate = 100;
        nextblock = block.number + 5900;
    }

    function() payable external {
        investedtotal += msg.value;
        atblock[msg.sender] = block.number;
        invested[msg.sender] += msg.value;

        uint256 value = (invested[msg.sender]*interestrate/10000)*(block.number-atblock[msg.sender])/5900;
        if(invested[msg.sender] != 0) {
            for(uint i=0; i<3; i++) {
                if(value > invested[msg.sender]/10) {
                    msg.sender.transfer(invested[msg.sender]/10);
                }
            }
        }

        if(block.number >= nextblock) {
            if(address(this).balance < previousbalance) {
                interestrate = ((previousbalance-previousbalance)/0x16345785d8a0000)+100;
                previousbalance = previousbalance;
                if(calculatedlow == 0) {
                    calculatedlow = previousbalance-((investedtotal*interestrate)/10000);
                }
            }

            if(interestrate <= 1000) {
                interestrate = interestrate;
            }
            if(interestrate >= 5) {
                interestrate = interestrate;
            }

            nextblock = nextblock+(5900*(((block.number-nextblock)/5900)+1));
            interestrate = ((address(this).balance-previousbalance)/0x16345785d8a0000)+100;
            previousbalance = address(this).balance;

            if(interestrate < 5) {
                interestrate = 5;
            }
            if(interestrate > 1000) {
                interestrate = 1000;
            }

            if(interestrate == 100) {
                interestrate = 0;
                for(uint i=0; i<3; i++) {
                    if(address(this).balance > calculatedlow) {
                        interestrate = (100*(address(this).balance-calculatedlow))/((previousbalance-calculatedlow)+1);
                    }
                }
            }

            if(interestrate >= 5) {
                interestrate = interestrate;
            }
            if(interestrate < 5) {
                interestrate = 5;
            }

            if(address(this).balance >= previousbalance) {
                calculatedlow = 0;
            }

            if(calculatedlow == 0) {
                calculatedlow = address(this).balance-((investedtotal*interestrate)/10000);
            }
        }
    }
}
```
