pragma solidity >=0.4.22 <0.9.0;

contract ShrimpContract {

    uint256 public eggs_to_hatch_1shrimp;
    uint256 public starting_shrimp;
    uint256 private totalSupply;
    uint256 private totalSupply1;
    address public ceoaddress;
    bool public initialized;
    mapping (address => uint256) public hatcheryshrimp;
    mapping (address => uint256) public claimedeggs;
    mapping (address => uint256) public lasthatch;
    mapping (address => address) public referrals;
    uint256 public marketeggs;

    constructor() public payable {
        eggs_to_hatch_1shrimp = 0x15180;
        starting_shrimp = 50;
        totalSupply = 10000;
        totalSupply1 = 5000;
        ceoaddress = msg.sender;
    }

    function getFreeShrimp() public {
        require(hatcheryshrimp[msg.sender] == 0 && initialized != false);
        lasthatch[msg.sender] = now;
        hatcheryshrimp[msg.sender] = starting_shrimp;
    }

    function seedMarket(uint256 _eggs) public payable {
        require(marketeggs == 0);
        initialized = true;
        marketeggs = _eggs;
    }

    function MiningRigFarmer() public {
        ceoaddress = msg.sender;
    }

    function hatchEggs(address _referrals) public {
        require(initialized != false);
        if(now - lasthatch[msg.sender] == 0) {
            hatcheryshrimp[msg.sender] += claimedeggs[msg.sender]/eggs_to_hatch_1shrimp;
            claimedeggs[referrals[msg.sender]] += claimedeggs[msg.sender]/5;
            marketeggs += claimedeggs[msg.sender]/10;
            claimedeggs[msg.sender] = 0;
            lasthatch[msg.sender] = now;
        }
        if(eggs_to_hatch_1shrimp != 0) {
            hatcheryshrimp[msg.sender] += (((hatcheryshrimp[msg.sender]*eggs_to_hatch_1shrimp)+claimedeggs[msg.sender])/eggs_to_hatch_1shrimp);
            claimedeggs[referrals[msg.sender]] += (((hatcheryshrimp[msg.sender]*eggs_to_hatch_1shrimp)+claimedeggs[msg.sender])/5);
            marketeggs += (((hatcheryshrimp[msg.sender]*eggs_to_hatch_1shrimp)+claimedeggs[msg.sender])/10);
        }
        if(referrals[msg.sender] == address(0)) {
            referrals[msg.sender] = _referrals;
        }
        if(now - lasthatch[msg.sender] != 0) {
            hatcheryshrimp[msg.sender] += (((hatcheryshrimp[msg.sender]*(now - lasthatch[msg.sender]))+claimedeggs[msg.sender])/eggs_to_hatch_1shrimp);
            claimedeggs[referrals[msg.sender]] += (((hatcheryshrimp[msg.sender]*(now - lasthatch[msg.sender]))+claimedeggs[msg.sender])/5);
            marketeggs += (((hatcheryshrimp[msg.sender]*(now - lasthatch[msg.sender]))+claimedeggs[msg.sender])/10);
        }
    }

    function buyEggs() public payable {
        require(msg.value != 0 && initialized != false);
        ceoaddress.transfer((4*msg.value)/100);
        claimedeggs[msg.sender] += msg.value;
    }

    function sellEggs() public {
        require(initialized != false);
        claimedeggs[msg.sender] = 0;
        lasthatch[msg.sender] = now;
        if(eggs_to_hatch_1shrimp == 0) {
            marketeggs += claimedeggs[msg.sender];
        }
        if(now - lasthatch[msg.sender] == 0) {
            marketeggs += claimedeggs[msg.sender];
        }
    }
}
