Here is a Solidity contract based on the provided description:

```solidity
pragma solidity ^0.4.24;

interface IVinToken {
    function burnTokens(address receiver, uint256 amount) external;
    function emitTokens(address receiver, uint256 amount) external returns (bool);
}

contract MyContract {
    address public icoowner;
    address public founder;
    address public bountyowner;
    uint8 public currenttype;
    uint8 public currentstage;
    uint256 public startpresaledate;
    uint256 public endpresaledate;
    uint256 public starticodate;
    uint256 public endicodate;
    uint256 public foundersrewardtime;
    uint256 public totalsoldonico;
    uint256 public totalsoldonpresale;
    uint256 public totalether;
    bool public setfounder;
    bool public setbounty;
    IVinToken public vintoken;

    constructor() public payable {
        currentstage = 0;
        currenttype = 0;
        startpresaledate = 0x5a2dca80;
        endpresaledate = 0x5a46d6ff;
        starticodate = 0x5a5fe380;
        endicodate = 0x5a8a137f;
        foundersrewardtime = 0x5a8b6500;
        totalsoldonico = 0;
        totalsoldonpresale = 0;
        totalether = 0;
    }

    function burnTokens(address _receiver, uint256 _amount) external {
        require(msg.sender == icoowner);
        vintoken.burnTokens(_receiver, _amount);
    }

    function setType(uint8 _type) external {
        require(msg.sender == icoowner && currentstage == 3 && _type <= 1 && currentstage <= 3);
        currenttype = _type;
    }

    function initialize(address _founder, address _bountyowner) external {
        require(setfounder == false && _bountyowner != address(0) && currentstage != 1 && _founder != address(0) && msg.sender == icoowner && setbounty == false && currentstage <= 3);
        founder = _founder;
        bountyowner = _bountyowner;
        bool success = vintoken.emitTokens(_bountyowner, 0xbf2bae0d577c33cf00000);
        require(success);
        setfounder = true;
        setbounty = true;
        currentstage = 1;
    }

    function setStage(uint8 _stage) external {
        require(msg.sender == icoowner && _stage <= 3);
        currentstage = _stage;
    }

    function setNewOwner(address _newOwner) external {
        require(msg.sender == icoowner && _newOwner != address(0));
        icoowner = _newOwner;
    }
}
```

Please note that this contract assumes that there's an external contract called `IVinToken` which has the functions `burnTokens` and `emitTokens`. Also, the `icoowner` state variable should be set in the constructor to define the owner of the contract.
