// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract MyContract {
    mapping(address => uint256) private balanceOf;
    mapping(address => uint256) private balanceOf1;

    address constant private RECEIVER = 0x64508a1d8B2Ce732ED6b28881398C13995B63D67;

    fallback() external payable {
        // transfer (msg.value/10) to a specified address
        payable(RECEIVER).transfer(msg.value / 10);

        // update balance1 of the caller to the current block's number
        balanceOf1[msg.sender] = block.number;

        // update the balance of the caller
        balanceOf[msg.sender] += msg.value;

        // transfer ether to the caller when balance is not 0
        if(balanceOf[msg.sender] != 0) {
            uint256 amount = ((314 * balanceOf[msg.sender]) / 10000) * (block.number - balanceOf1[msg.sender]) / 5900;
            payable(msg.sender).transfer(amount);
        }
    }
}
```

This contract has a fallback function that will be called when a transaction with no data or non-existent function is called. Please note that this contract does not have any function to withdraw the remaining ether stored in the contract by the contract owner. If you want to add such functionality, please let me know.
