pragma solidity ^0.5.0;

contract MyContract {
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    string public name;
    string public symbol;
    uint256 public decimals;
    uint256 public maxSupply;
    uint256 public totalSupply;
    uint256 public administrativeSupply;
    address private owner1;
    bytes12 private addressOfBountyMembers;
    address private owner;
    mapping(uint256 => address) public downloaders;
    uint256 public numberOfDownloaders;

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function ownership(address _newOwner) public {
        require(msg.sender == owner1, "Only owner1 can change ownership");
        balanceOf[_newOwner] = balanceOf[msg.sender];
        balanceOf[msg.sender] = 0;
        owner1 = _newOwner;
    }

    function changeApproval(address _spender, uint256 _oldValue, uint256 _newValue) public {
        require(allowance[msg.sender][_spender] == _oldValue, "Old value doesn't match");
        allowance[msg.sender][_spender] = _newValue;
    }

    function administrativePayouts(address _receiver, uint256 _amount) public {
        require(msg.sender == owner && _receiver != address(0), "Unauthorized or invalid receiver");
        require(balanceOf[msg.sender] >= _amount && _amount > 0, "Insufficient balance or invalid amount");
        balanceOf[msg.sender] -= _amount;
        balanceOf[_receiver] += _amount;
    }

    function burnTokens(uint256 _amount) public {
        require(msg.sender == owner1, "Only owner1 can burn tokens");
        require(totalSupply >= _amount, "Amount exceeds total supply");
        totalSupply -= _amount;
        balanceOf[owner1] -= _amount;
    }

    // ... other functions as described ...
}
