Based on the problem description, here is one possible Solidity contract that could be used:

```solidity
pragma solidity >=0.4.22 <0.9.0;

contract MyContract {
    address public owner;
    mapping(address => uint256) public isERC777;
    mapping(address => mapping(address => uint256)) public isApproved;
    mapping(address => mapping(address => uint256)) public balanceOf;
    mapping(address => uint256) private balanceOf2;
    mapping(address => uint256) public isSpender;
    address public latestSpender;

    constructor() public {
        owner = msg.sender;
    }

    function setERC777(address _addr) public {
        require(msg.sender == owner);
        isERC777[_addr] = 1;
    }

    function unsetERC777(address _addr) public {
        require(msg.sender == owner);
        isERC777[_addr] = 0;
    }

    function tokensReceived(address _from, address _to, address _operator, uint256 _amount, bytes memory _userData, bytes memory _operatorData) public {
        balanceOf[_from][_to] += _amount;
        balanceOf2[_from] += _amount;
        if(isERC777[_from] == 0) {
            isERC777[_from] = 1;
        }
    }

    function removeSpender(address _spender) public {
        require(msg.sender == owner);
        isSpender[_spender] = 0;
    }

    function approve(address _spender) public {
        if(isSpender[_spender] != 0) {
            isApproved[msg.sender][_spender] = 1;
        }
    }

    function unapprove(address _spender) public {
        isApproved[msg.sender][_spender] = 0;
    }

    function transferOwnership(address _newOwner) public {
        require(msg.sender == owner);
        owner = _newOwner;
    }

    function addSpender(address _spender) public {
        require(msg.sender == owner && _spender != address(0));
        isSpender[_spender] = 1;
        latestSpender = _spender;
    }

    function tokenFallback(address _from, uint256 _value, bytes memory _data) public {
        balanceOf[msg.sender][_from] += _value;
        balanceOf2[msg.sender] += _value;
    }

    function transfer(address _from, address _to, address _spender, uint256 _value) public {
        require(_value > 0 && isSpender[msg.sender] != 0 && isApproved[_to][msg.sender] != 0);
        balanceOf[_from][_to] -= _value;
        balanceOf[_from][_spender] += _value;
    }

    // other functions omitted for brevity
}
```
This code does not include the deposit and withdraw functions as they require interaction with ether and external contracts, which is beyond the scope of this problem. Remember to always test and audit smart contract code thoroughly before deploying.
