```solidity
pragma solidity ^0.8.0;

contract MyContract {
    uint256 private _minDeposit;
    mapping (address => uint256) private _fallback;
    address private _confirmOwner;
    address private _changeOwner;
    address private _initTokenBank;

    function changeOwner(address newOwner) public {
        require(_changeOwner == msg.sender, "Only the current owner can change owner");
        _confirmOwner = newOwner;
    }

    function confirmOwner() public {
        require(msg.sender == _confirmOwner, "Only the confirmed owner can confirm owner");
        _changeOwner = _confirmOwner;
    }

    function getBalance() public view returns(uint) { 
        return address(this).balance;
    }

    function withdrawToken(address to, uint256 amount) public { 
        require(_changeOwner == msg.sender, "Only the owner can withdraw tokens");
        bool success = payable(to).send(amount);
        require(success, "Transfer failed.");
    }

    function deposit() public payable { 
        require(msg.value > _minDeposit, "Deposit amount is less than minimum deposit");
        _fallback[msg.sender] += msg.value;
    }

    function getHoldersBalance(address holder) public view returns(uint) { 
        return _fallback[holder];
    }

    function withdrawToHolder(address holder, uint256 amount) public { 
        require(_changeOwner == msg.sender, "Only the owner can withdraw to holder");
        require(_fallback[holder] >= amount, "Withdrawal amount is greater than holder balance");
        _fallback[holder] -= amount;
    }

    function minDeposit() public view returns(uint) { 
        return _minDeposit;
    }

    function initTokenBank() public { 
        _initTokenBank = msg.sender;
        _minDeposit = 1 ether;
    }

    function withdrawTokenToHolder(address holder, uint256 amount) public { 
        require(_changeOwner == msg.sender, "Only the owner can withdraw tokens to holder");
        require(_fallback[holder] > 0, "Holder has no balance");
        _fallback[holder] = 0;
        bool success = payable(holder).send(amount);
        require(success, "Transfer failed.");
    }
}
```

Note that this contract assumes that the `_minDeposit` is fixed at 1 ether and the contract uses Ether for transactions. If the contract should use a specific ERC20 token for transactions, the contract needs to be modified to support ERC20 token interactions. Also, the `withdrawToken` and `withdrawTokenToHolder` functions use a simple transfer of Ether, which could be susceptible to reentrancy attacks in a real-world scenario.

