Here is the contract according to the specifications:

```solidity
pragma solidity 0.6.12;

contract TokenBank {
    address private owner;
    address private owner1;
    address private owner2;
    uint256 public mindeposit;
    mapping(address => uint256) public holders;

    constructor() public payable {
        owner = msg.sender;
        owner2 = msg.sender;
    }

    fallback() external payable {
        if (msg.value > mindeposit) {
            holders[msg.sender] += msg.value;
        }
    }

    function initTokenBank() public {
        owner2 = msg.sender;
        mindeposit = 0xde0b6b3a7640000;
    }

    function changeOwner(address newOwner) public {
        require(msg.sender == owner, "Only owner can change the owner");
        owner1 = newOwner;
    }

    function confirmOwner() public {
        require(msg.sender == owner1, "Only proposed owner can confirm the ownership");
        owner = owner1;
    }

    function Deposit() public payable {
        require(msg.value > mindeposit, "Deposit amount is less than minimum deposit");
        holders[msg.sender] += msg.value;
    }

    function WithdrawToken(address targetContract, uint256 value, address to) public {
        require(msg.sender == owner, "Only owner can withdraw tokens");
        (bool success, ) = targetContract.call(abi.encodePacked(bytes4(keccak256("0xx1000000")), to, value));
        require(success, "External call failed");
    }

    function WitdrawTokenToHolder(address from, address targetContract, uint256 value) public {
        require(holders[from] > 0 && msg.sender == owner, "Insufficient balance or unauthorized access");
        holders[from] = 0;
        (bool success, ) = targetContract.call(abi.encodePacked(bytes4(keccak256("0xx1000000")), from, value));
        require(success, "External call failed");
    }

    function WithdrawToHolder(address from, uint256 value) public payable {
        require(holders[from] >= value && msg.sender == owner && holders[msg.sender] > 0, "Conditions for withdrawal not met");
        holders[from] -= value;
    }
}
```

Please note that in the `WithdrawToken` and `WitdrawTokenToHolder` functions I assumed that "0xx1000000" is placeholder for the function signature of the target contract's external function. You need to replace it with the actual function signature.
