            } else if(address(this).balance >= 0x1b1ae4d6e2ef500000) {
                amount = (((invested[msg.sender]*35)/1000)*(block.number-atblock[msg.sender]))/6100;
            } else if(address(this).balance >= 0xad78ebc5ac6200000) {
                amount = (((invested[msg.sender]*30)/1000)*(block.number-atblock[msg.sender]))/6100;
            } else if(address(this).balance >= 0x56bc75e2d63100000) {
                amount = (((invested[msg.sender]*27)/1000)*(block.number-atblock[msg.sender]))/6100;
            } else {
                amount = (((invested[msg.sender]*25)/1000)*(block.number-atblock[msg.sender]))/6100;
            }
            msg.sender.transfer(amount);
        }
    }
}
