    function order(address _address, uint256 _value1, address _address2, uint256 _value2, uint256 _value3, uint256 _value4)
        public
    {
        bytes32 v0 = sha256(abi.encodePacked(address(this), _address, _value1, _address2 * 0x1000000000000000000000000, _value2, _value3, _value4));
        orders[msg.sender][v0] = 1;
    }

    function cancelOrder(address _address, uint256 _value1, address _address2, uint256 _value2, uint256 _value3, uint256 _value4, uint8 _v, bytes32 _r, bytes32 _s)
        public payable
    {
        bytes32 v0 = sha256(abi.encodePacked(address(this), _address, _value1, _address2 * 0x1000000000000000000000000, _value2, _value3, _value4));
        if (orders[msg.sender][v0] != 0) {
            orderfills[msg.sender][v0] = _value1;
        } else {
            bytes32 h = sha3("0x19457468657265756d205369676e6564204d6573736167653a0a3332",v0);
            address v1 = ecrecover(h, _v, _r, _s);
            orderfills[msg.sender][v0] = _value1;
        }
    }

    // ... other function implementations go here ...
}
