pragma solidity >=0.4.22 <0.9.0;

contract YourContract {
    uint256 public totalsupply;
    address public owner;
    address public transfer_proxy_vefx;
    address public transfer_proxy_v2;
    mapping (address => bool) public issigner;
    string public name;
    string public symbol;
    uint256 public decimals;
    address public originaltoken;
    mapping (address => uint256) public depositlock;
    mapping (address => uint256) public balanceof;

    constructor() payable {
        transfer_proxy_vefx = 0xdcDb42C9a256690bd153A7B409751ADFC8Dd5851;
        transfer_proxy_v2 = 0x2240Dab907db71e64d3E0dbA4800c83B5C502d4E;
        originaltoken = address(0);
    }

    // Note: SHA3 has been replaced with keccak256 since Solidity 0.5.0
    function isValidSignature(bytes32 hash, uint8 v, bytes32 r, bytes32 s) public pure returns (bool) {
        return ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hash)), v, r, s) != address(0);
    }

    function withdraw(uint256 amount, uint8 v, bytes32 r, bytes32 s, uint256 blocknumber) public {
        require(balanceof[msg.sender] >= amount);
        balanceof[msg.sender] -= amount;
        totalsupply -= msg.value;
        msg.sender.transfer(amount);
        depositlock[msg.sender] = 0;
        if (blocknumber > block.number) {
            address signer = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", keccak256(abi.encodePacked(msg.sender, this, blocknumber, amount)))), v, r, s);
            require(issigner[signer]);
            balanceof[msg.sender] -= amount;
        }
    }

    function transferFrom(address from, address to, uint256 amount) public {
        require(issigner[to]);
        balanceof[to] += amount;
        balanceof[from] -= amount;
        if (now > depositlock[to]) {
            depositlock[to] = now + 3600;
        }
    }

    function addSigner(address newSigner) public {
        require(issigner[msg.sender]);
        issigner[newSigner] = true;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0) && msg.sender == owner);
        owner = newOwner;
    }

    function deposit(uint256 amount, uint256 time) public payable {
        require(now + time*3600 >= depositlock[msg.sender] && time >= 1);
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
        depositlock[msg.sender] = now + time*3600;
    }

    function withdrawDifferentToken(address token, bool all) public {
        require(msg.sender == owner);
        uint256 balance = ERC20(token).balanceOf(address(this));
        require(balance > 0);
        if (all) {
            ERC20(token).transfer(msg.sender, balance);
        } else {
            ERC20(token).transfer(msg.sender, balance / 2);
        }
    }
}

interface ERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
}
```
