Based on your description, here is the Solidity contract:

```solidity
pragma solidity ^0.8.0;

contract MyContract {
    uint256 public totalsupply;
    mapping(address => uint256) private balanceOf2;
    mapping(address => mapping(address => uint256)) public allowance;
    address public owner;
    string public name;
    string public symbol;
    uint256 public decimals;
    uint256 public buypriceinwei;
    uint256 public sellpriceinwei;
    uint256 public minbalanceforaccounts;
    address public companywallet;
    mapping(address => uint256) public balanceof;
    mapping(address => bool) public isfrozen;

    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    function approve(address _to, uint256 _value) public {
        allowance[msg.sender][_to] = _value;
    }

    function setCompanyWallet(address _wallet) public onlyOwner {
        require(_wallet != address(0));
        companywallet = _wallet;
    }

    function setBuyPrice(uint256 _price) public onlyOwner {
        require(_price > 0);
        buypriceinwei = _price;
    }

    function destroy() public onlyOwner {
        selfdestruct(payable(owner));
    }

    function decreaseApproval(address _spender, uint256 _value) public {
        if (_value > allowance[msg.sender][_spender]) {
            allowance[msg.sender][_spender] = 0;
        } else {
            allowance[msg.sender][_spender] -= _value;
        }
    }

    function forwardFundToCompanyWallet(uint256 _amount) public onlyOwner {
        payable(companywallet).transfer(_amount);
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_to != address(0) && _value <= allowance[_from][msg.sender] && _value <= balanceOf2[_from]);
        balanceOf2[_from] -= _value;
        balanceOf2[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }

    function setMinBalance(uint256 _minimumBalance) public onlyOwner {
        minbalanceforaccounts = _minimumBalance;
    }

    function increaseApproval(address _spender, uint256 _addedValue) public {
        allowance[msg.sender][_spender] += _addedValue;
    }

    function freezeAccount(address _account, bool freeze) public onlyOwner {
        isfrozen[_account] = freeze;
    }

    function destroyAndSend(address _recipient) public onlyOwner {
        selfdestruct(payable(_recipient));
    }

    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0));
        owner = newOwner;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0) && !isfrozen[msg.sender] && !isfrozen[_to] && _value <= balanceof[msg.sender]);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function setSellPrice(uint256 _price) public onlyOwner {
        require(_price > 0);
        sellpriceinwei = _price;
    }

    function distribute(address _to, uint256 _value) public onlyOwner {
        require(_to != address(0));
        balanceof[address(this)] -= _value;
        balanceof[_to] += _value;
    }

    fallback() payable external {
        buy();
    }

    function buy() payable public {
        uint256 amount = msg.value / buypriceinwei;
        require(amount > 0);
        balanceof[address(this)] -= amount;
        balanceof[msg.sender] += amount;
    }
}
```

Please note that, this contract is a simple representation of your requirements. It doesn't include any event logging or error handling (via `revert` or `require`). It's also missing some important security considerations such as preventing overflow/underflow attacks. Please use this as a base and not a final product.
