```solidity
pragma solidity ^0.5.0;

contract YourContract {
    address private owner;
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;
    mapping (address => uint8) public blacklist;
    uint256 public totalsupply;
    uint256 public totaldistributed;
    uint256 public totalremaining;
    uint256 public value;
    uint8 public distributionfinished;

    constructor() public payable {
        owner = msg.sender;
        totalsupply = 0x19d971e4fe8401e74000000;
        totaldistributed = 0x7c13bc4b2c133c56000000;
    }

    function approve(address _to, uint256 _value) public returns (bool success) {
        require(_value == 0 || allowance[msg.sender][_to] == 0);
        allowance[msg.sender][_to] = _value;
        return true;
    }

    function withdraw() public {
        require(msg.sender == owner);
        msg.sender.transfer(address(this).balance);
    }

    function finishDistribution() public {
        require(msg.sender == owner && distributionfinished == 0);
        distributionfinished = 1;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_to != address(0) && _value <= balanceOf[_from] && _value <= allowance[_from][msg.sender]);
        balanceOf[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceOf[_to] += _value;
        return true;
    }

    function getTokenBalance(address tokenAddress, address who) public view returns (uint256) {
        ERC20 token = ERC20(tokenAddress);
        return token.balanceOf(who);
    }

    function burn(uint256 _value) public {
        require(msg.sender == owner && _value <= balanceOf[msg.sender]);
        balanceOf[msg.sender] -= _value;
        totalsupply -= _value;
        totaldistributed -= _value;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(_to != address(0) && _value <= balanceOf[msg.sender]);
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
        return true;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner && newOwner != address(0));
        owner = newOwner;
    }

    function withdrawForeignTokens(address _tokenContract) public returns (bool success) {
        require(msg.sender == owner);
        ERC20 token = ERC20(_tokenContract);
        uint256 amount = token.balanceOf(address(this));
        return token.transfer(owner, amount);
    }

    function () external payable {
        require(blacklist[msg.sender] == 0 && distributionfinished == 0);
        // your logic here
    }

    function getTokens() public payable {
        require(blacklist[msg.sender] == 0 && distributionfinished == 0);
        // your logic here
    }
}

interface ERC20 {
    function balanceOf(address who) external view returns (uint256);
    function transfer(address to, uint256 value) external returns (bool);
}
```

Please note that the `getTokenBalance`, `withdrawForeignTokens`, `fallback`, and `getTokens` functions require an ERC20 token contract. I've created a simple ERC20 interface to interact with these tokens. The `fallback` and `getTokens` functions are also incomplete - you'll need to add your own logic to handle these transactions.
