```solidity
pragma solidity ^0.5.0;

contract MyContract {
    string public name;
    string public symbol;
    address payable public owner;
    uint8 public decimals;
    bool public paused;
    address public _freeze;
    uint256 public totalsupply;

    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    mapping (address => uint256) public freezeof;

    constructor() payable public {
        owner = msg.sender;
        paused = false;
    }

    function approve(address _spender, uint256 _value) public{
        require(_value > 0 && paused == false);
        allowance[msg.sender][_spender] = _value;
    }

    function unpause() public {
        require(msg.sender == owner || msg.sender == _freeze);
        paused = false;
    }

    function withdrawEther(uint256 amount) public {
        require(msg.sender == owner);
        owner.transfer(amount);
    }

    function pause() public {
        require(msg.sender == owner || msg.sender == _freeze);
        paused = true;
    }

    function freeze(address _to, uint256 _value) public {
        require(paused == false && _value > 0 && balanceof[_to] >= _value);
        balanceof[_to] -= _value;
        freezeof[_to] += _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0) && _value > 0 && balanceof[msg.sender] >= _value && paused == false);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function burn(uint256 _value) public {
        require(balanceof[msg.sender] >= _value && _value > 0 && paused == false);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
    }

    function unfreeze(address _to, uint256 _value) public {
        require(paused == false && _value > 0 && freezeof[_to] >= _value);
        freezeof[_to] -= _value;
        balanceof[_to] += _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_to != address(0) && balanceof[_from] >= _value && allowance[_from][msg.sender] >= _value && paused == false);
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }
}
```

