Here is the Solidity code for the contract:

```solidity
pragma solidity ^0.5.0;

contract OrderContract {
    address public admin;
    address public feeaccount;
    address public accountlevelsaddr;
    uint256 public feemake;
    uint256 public feetake;
    uint256 public feerebate;
    mapping (address => mapping (address => uint256)) public balanceof;
    mapping (address => mapping (bytes32 => uint256)) public orders;
    mapping (address => mapping (bytes32 => uint256)) public orderfills;

    function order(address _tokenGet, uint256 _amountGet, address _tokenGive, uint256 _amountGive, uint256 _expires, uint256 _nonce) public {
        bytes32 v0 = sha256(abi.encodePacked(this, _tokenGet, _amountGet, _tokenGive, _amountGive, _expires, _nonce));
        orders[msg.sender][v0] = 1;
    }

    function cancelOrder(address _tokenGet, uint256 _amountGet, address _tokenGive, uint256 _amountGive, uint256 _expires, uint256 _nonce, uint8 _v, bytes32 _r, bytes32 _s) public {
        bytes32 v0 = sha256(abi.encodePacked(this, _tokenGet, _amountGet, _tokenGive, _amountGive, _expires, _nonce));
        if (orders[msg.sender][v0] != 0) {
            orderfills[msg.sender][v0] = _amountGet;
        } else {
            address v1 = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", v0)), _v, _r, _s);
            if (v1 != address(0)) {
                orderfills[msg.sender][v0] = _amountGet;
            }
        }
    }

    function changeFeeMake(uint256 _feeMake) public {
        require(msg.sender == admin);
        feemake = _feeMake;
    }

    function withdraw(uint256 _amount) public {
        require(balanceof[address(0)][msg.sender] >= _amount);
        balanceof[address(0)][msg.sender] -= _amount;
        msg.sender.transfer(_amount);
    }

    function changeFeeRebate(uint256 _feeRebate) public {
        require(msg.sender == admin);
        feerebate = _feeRebate;
    }

    function changeFeeAccount(address _feeAccount) public {
        require(msg.sender == admin);
        feeaccount = _feeAccount;
    }

    // Remaining functions are omitted for brevity.
}
```

