Here is a Solidity contract that follows the logic described:

```solidity
pragma solidity ^0.5.16;

contract FundRaising {
    address public beneficiary;
    uint256 public fundinggoal;
    uint256 public amountraised;
    uint256 public deadline;
    uint256 public price;
    address public tokenreward;
    mapping(address => uint256) public balanceof;
    bool private paused;
    bool private paused1;

    constructor() public payable {
        beneficiary = msg.sender;
        fundinggoal = 0;
        amountraised = 0;
        deadline = now + 30 days;
        price = 1 ether;
        paused = false;
        paused1 = false;
    }

    function() external payable {
        require(!paused, "Contract is paused");
        balanceof[msg.sender] += msg.value;
        amountraised += msg.value;
        if (price != 0) {
            tokenreward.call(bytes4(keccak256("transfer(address,uint256)")), msg.sender, (msg.value*0xde0b6b3a7640000)/price);
        }
    }

    function checkGoalReached() public {
        require(now >= deadline, "Deadline not reached");
        paused = true;
        if (amountraised >= fundinggoal) {
            paused1 = true;
        }
    }

    function safeWithdrawal() public {
        require(now >= deadline, "Deadline not reached");
        if (!paused1) {
            uint amount = balanceof[msg.sender];
            balanceof[msg.sender] = 0;
            if (amount > 0) {
                if (msg.sender.send(amount)) {
                    balanceof[msg.sender] = amount;
                }
            }
        } else if (paused1 && msg.sender == beneficiary) {
            if (beneficiary.send(amountraised)) {
                paused1 = false;
            }
        }
    }
}
```

