```solidity
pragma solidity ^0.6.0;

contract OrderContract {
    mapping (address => uint256) public admin;
    address public feeaccount;
    address public accountlevelsaddr;
    uint256 public feemake;
    uint256 public feetake;
    uint256 public feerebate;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (bytes32 => uint256)) public orders;
    mapping (address => mapping (bytes32 => uint256)) public orderfills;

    function order(address _address, uint256 _value1, address _address2, uint256 _value2, uint256 _value3, uint256 _value4) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), _address, _value1, _address2, _value2, _value3, _value4));
        orders[msg.sender][v0] = 1;
    }

    function cancelOrder(address _address, uint256 _value1, address _address2, uint256 _value2, uint256 _value3, uint256 _value4, uint8 _v, bytes32 _r, bytes32 _s) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), _address, _value1, _address2, _value2, _value3, _value4));
        if (orders[msg.sender][v0] != 0) {
            orderfills[msg.sender][v0] = _value1;
        } else {
            address v1 = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", v0)), _v, _r, _s);
            orderfills[msg.sender][v0] = _value1;
        }
    }

    function changeFeeMake(uint256 _value) public {
        require(admin[msg.sender] != 0 && _value <= feemake);
        feemake = _value;
    }

    function withdraw(uint256 _value) public {
        require(_value <= admin[msg.sender]);
        admin[msg.sender] -= _value;
        msg.sender.transfer(_value);
    }

    function changeFeeAccount(address _address) public {
        require(admin[msg.sender] != 0);
        feeaccount = _address;
    }

    function changeFeeRebate(uint256 _value) public {
        require(admin[msg.sender] != 0 && _value <= feetake && _value >= feerebate);
        feerebate = _value;
    }

    function changeAdmin(address _address) public {
        require(admin[msg.sender] != 0);
        admin[_address] = admin[msg.sender];
        admin[msg.sender] = 0;
    }

    function changeAccountLevelsAddr(address _address) public {
        require(admin[msg.sender] != 0);
        accountlevelsaddr = _address;
    }

    function deposit() public payable {
        require((msg.value + admin[msg.sender]) >= admin[msg.sender]);
        admin[msg.sender] += msg.value;
    }
}
```
