Here is a Solidity contract based on the provided specifications:

```solidity
pragma solidity ^0.8.0;

contract Token {
    address private owner;
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => uint8) public blacklist;
    uint256 public totalsupply;
    uint256 public totaldistributed;
    uint256 public totalremaining;
    uint256 public value;
    uint8 public distributionfinished;

    constructor() payable {
        owner = msg.sender;
        totalsupply = 0xadb53acfa41aee12000000;
        totaldistributed = 0xa56fa5b99019a5c8000000;
    }

    function approve(address _spender, uint256 _value) public {
        require(_value == 0 || allowance[msg.sender][_spender] == 0);
        allowance[msg.sender][_spender] = _value;
    }

    function withdraw() public {
        require(msg.sender == owner);
        payable(owner).transfer(address(this).balance);
    }

    function finishDistribution() public {
        require(msg.sender == owner && distributionfinished == 0);
        distributionfinished = 1;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= balanceOf[_from]);
        require(_value <= allowance[_from][msg.sender]);
        require(_to != address(0));
        balanceOf[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function getTokenBalance(address _tokenContract, address _address) public view returns(uint256) {
        return ERC20(_tokenContract).balanceOf(_address);
    }

    function burn(uint256 _value) public {
        require(msg.sender == owner);
        require(_value <= balanceOf[msg.sender]);
        balanceOf[msg.sender] -= _value;
        totalsupply -= _value;
        totaldistributed -= _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0));
        require(_value <= balanceOf[msg.sender]);
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0));
        require(msg.sender == owner);
        owner = newOwner;
    }

    function withdrawForeignTokens(address _tokenContract) public {
        require(msg.sender == owner);
        ERC20 erc20 = ERC20(_tokenContract);
        uint256 amount = erc20.balanceOf(address(this));
        erc20.transfer(owner, amount);
    }

    fallback() external payable {
        require(distributionfinished == 0 && blacklist[msg.sender] == 0);
        require(msg.value <= totalremaining);
        value = msg.value;
        totaldistributed += value;
        totalremaining -= value;
        balanceOf[msg.sender] += value;
        if(value > 0) {
            blacklist[msg.sender] = 1;
        }
        if(totaldistributed >= totalsupply) {
            distributionfinished = 1;
        }
    }

    function getTokens() public payable {
        require(distributionfinished == 0 && blacklist[msg.sender] == 0);
        require(msg.value <= totalremaining);
        value = msg.value;
        totaldistributed += value;
        totalremaining -= value;
        balanceOf[msg.sender] += value;
        if(value > 0) {
            blacklist[msg.sender] = 1;
        }
        if(totaldistributed >= totalsupply) {
            distributionfinished = 1;
        }
    }
}

interface ERC20 {
    function balanceOf(address _address) external view returns(uint256);
    function transfer(address _to, uint256 _value) external returns(bool);
}
```

This contract assumes the use of ERC20 tokens and includes a function to interact with ERC20 contracts. This contract also includes a fallback function to handle incoming ether transactions and distribute tokens accordingly. Please note that this contract has not been tested and should not be used in a production environment without thorough testing and auditing.
