```solidity
pragma solidity ^0.8.0;

contract MyContract {
    address public owner;
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    uint256 public sellprice;
    uint256 public buyprice;
    mapping (address => bool) public frozenaccount;

    constructor() payable {
        owner = msg.sender;
        decimals = 18;
    }

    function setPrices(uint256 _sellprice, uint256 _buyprice) public {
        require(msg.sender == owner);
        sellprice = _sellprice;
        buyprice = _buyprice;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function burn(uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
        return true;
    }

    function mintToken(address target, uint256 mintedAmount) public {
        require(msg.sender == owner);
        balanceof[target] += mintedAmount;
        totalsupply += mintedAmount;
    }

    function burnFrom(address _from, uint256 _value) public returns (bool success) {
        require(_value <= balanceof[_from]);
        require(_value <= allowance[_from][msg.sender]);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        totalsupply -= _value;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);
        allowance[_from][msg.sender] -= _value;
        require(_value <= balanceof[_from]);
        require(!frozenaccount[_to]);
        require(_to != address(0));
        require(balanceof[_to] + _value >= balanceof[_to]);
        require(!frozenaccount[_from]);
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function freezeAccount(address target, bool freeze) public {
        require(msg.sender == owner);
        frozenaccount[target] = freeze;
    }

    function buy() public payable {
        require(!frozenaccount[msg.sender]);
        require(!frozenaccount[address(this)]);
        require(buyprice != 0);
        uint amount = msg.value / buyprice;
        require(balanceof[address(this)] >= amount);
        require(balanceof[msg.sender] + amount >= balanceof[msg.sender]);
        require(msg.sender != address(0));
        balanceof[address(this)] -= amount;
        balanceof[msg.sender] += amount;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner);
        owner = newOwner;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value);
        require(balanceof[_to] + _value >= balanceof[_to]);
        require(!frozenaccount[_to]);
        require(!frozenaccount[msg.sender]);
        require(_to != address(0));
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        bytes memory empty;
        if (_extraData.length == 0) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, empty));
            return success;
        }
        if (_extraData.length <= 32) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            return success;
        }
        if (_extraData.length <= 64) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            return success;
        }
        if (_extraData.length <= 96) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            return success;
        }
        if (_extraData.length <= 128) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            return success;
        }
        if (_extraData.length > 128) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            return success;
        }
        return false;
    }

    function sell(uint256 amount) public {
        require(balanceof[address(this)] >= amount * sellprice);
        require(amount <= balanceof[msg.sender]);
        require(!frozenaccount[address(this)]);
        require(balanceof[address(this)] + amount >= balanceof[address(this)]);
        require(address(this) != address(0));
        require(!frozenaccount[msg.sender]);
        balanceof[msg.sender] -= amount;
        balanceof[address(this)] += amount;
        payable(msg.sender).transfer(amount * sellprice);
    }
}
```

