Here's the contract code in Solidity based on the logic you provided:

```
pragma solidity ^0.5.0;

contract YourContract {
    address private owner;
    bool public paused;
    address private owner1;
    address public voken;
    address public public_sale;
    uint256 private price;
    uint256 private rate;
    uint256 public txs;
    mapping (address => uint256) private balanceOf;

    constructor() public payable {
        owner = msg.sender;
        voken = 0xFfFAb974088Bd5bF3d7E6F522e93Dd7861264cDB;
        public_sale = 0xd4260e4Bfb354259F5e30279cb0D7F784Ea5f37A;
        price = 0xde0b6b3a7640000;
        rate = 0x3baa0c40;
        paused = false;
    }

    function acceptOwnership() external {
        require(msg.sender != address(0));
        owner = msg.sender;
        owner1 = address(0);
    }

    function setPaused(bool _paused) external {
        require(msg.sender == owner);
        paused = _paused;
    }

    function withdrawEther(address to, uint256 amount) external {
        require(msg.sender == owner && to != address(0) && address(this).balance >= amount);
        (bool success, ) = to.call.value(amount)("");
        require(success);
    }

    function transferOwnership(address newOwner) external {
        require(msg.sender == owner && newOwner != address(0));
        owner1 = newOwner;
    }

    function rescueTokens(address token, address to, uint256 amount) external {
        require(msg.sender == owner && to != address(0));
        uint256 v0 = MyToken(token).balanceOf(address(this));
        require(v0 >= amount);
        MyToken(token).transfer(to, amount);
    }

    function() external payable {
        require(!paused && msg.value >= price);
        uint256 v0 = MyToken(voken).balanceOf(msg.sender);
        require(v0 > 0);
        bool v1 = MyToken(voken).whitelisted(msg.sender);
        require(balanceOf[msg.sender] == 0 && !v1);
        uint256 v2 = MyToken(public_sale).status();
        require((1 + txs) >= txs && msg.value >= 0 && v2 > 0);
        txs += 1;
        balanceOf[msg.sender] = 1;
        (bool success, ) = msg.sender.call.value(msg.value - 0)("");
        require(success);
        MyToken(voken).mint(msg.sender, rate);
    }
}

interface MyToken {
    function balanceOf(address tokenOwner) external view returns (uint256 balance);
    function transfer(address to, uint256 tokens) external returns (bool success);
    function mint(address to, uint256 tokens) external;
    function whitelisted(address account) external view returns (bool);
    function status() external view returns (uint256);
}
```

This contract has the following functions: constructor, acceptOwnership, setPaused, withdrawEther, transferOwnership, rescueTokens and a fallback function. The contract also interacts with an external token contract using the MyToken interface. 

Please replace "YourContract" and "MyToken" with your actual contract and token names. Also make sure to test this contract thoroughly before using it in a production environment.
