    }

    function transferFrom(address _from, address _to, uint256 _value) public payable {
        require(balanceOf[_from] >= _value, 'Insufficient balance');
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        if(allowance[_from][msg.sender] != 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff) {
            require(allowance[_from][msg.sender] >= _value, 'Insufficient allowance');
            allowance[_from][msg.sender] -= _value;
        }
    }

    function transfer(address _to, uint256 _value) public payable {
        require(balanceOf[msg.sender] >= _value, 'Insufficient balance');
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function permit(address owner, address spender, uint256 value, uint256 deadline, uint8 v, bytes32 r, bytes32 s) public payable {
        require(deadline >= block.timestamp, 'Permit expired');
        bytes32 digest = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", keccak256(abi.encodePacked(owner, spender, value, nonces[owner]++, deadline))));
        address recoveredAddress = ecrecover(digest, v, r, s);
        require(recoveredAddress != address(0) && recoveredAddress == owner, 'Invalid signature');
        allowance[owner][spender] = value;
    }

