Here's a Solidity contract that implements the provided logic:

```solidity
pragma solidity >=0.7.0 <0.9.0;

contract Crowdfunding {
    address public beneficiary;
    uint256 public fundinggoal;
    uint256 public amountraised;
    uint256 public deadline;
    uint256 public price;
    address public tokenreward;
    mapping(address => uint256) public balanceof;
    bool private paused;
    bool private paused1;

    constructor(address _beneficiary, uint256 _fundinggoal, uint256 _deadline, uint256 _price, address _tokenreward) payable {
        beneficiary = _beneficiary;
        fundinggoal = _fundinggoal;
        deadline = _deadline;
        price = _price;
        tokenreward = _tokenreward;
        paused = false;
        paused1 = false;
    }

    fallback() external payable {
        require(!paused, "Contract is paused");
        balanceof[msg.sender] += msg.value;
        amountraised += msg.value;
        if(price != 0) {
            TokenReward(tokenreward).transfer(msg.sender, msg.value / price);
        }
    }

    function checkGoalReached() public {
        require(block.timestamp >= deadline, "Deadline not reached");
        paused = true;
        if(amountraised >= fundinggoal){
            paused1 = true;
        }
    }

    function safeWithdrawal() public {
        require(block.timestamp >= deadline, "Deadline not reached");
        if(!paused1){
            uint amount = balanceof[msg.sender];
            balanceof[msg.sender] = 0;
            if(amount > 0) {
                payable(msg.sender).transfer(amount);
            }
        } else if(paused1 && msg.sender == beneficiary){
            payable(beneficiary).transfer(amountraised);
            paused1 = false;
        }
    }
}

interface TokenReward {
    function transfer(address recipient, uint256 amount) external;
}
```

Please note that this contract assumes that the external contract `TokenReward` has a `transfer` function that takes an address and a uint256 as parameters. Also, this contract does not check whether the transfers are successful or not. Please consider adding some error checking for the transfers.
