pragma solidity ^0.5.0;

contract MyContract {
    address public admin;
    address private last_completed_migration;
    uint256 private last_completed_migration1;
    address public implementation;

    modifier onlyAdmin() {
        require(msg.sender == admin, "Caller is not the admin");
        _;
