    }

    function upgradeTo(address newAddress) external {
        if (msg.sender == admin) {
            last_completed_migration = newAddress;
        } else {
            (bool success,) = last_completed_migration.delegatecall(msg.data);
            require(success);
        }
    }

    function changeAdmin(address newAdmin) external {
        if (msg.sender == admin) {
            last_completed_migration1 = uint256(newAdmin);
        } else {
            (bool success,) = last_completed_migration.delegatecall(msg.data);
            require(success);
        }
    }

    function upgradeToAndCall(address newAddress, bytes memory data) public payable {
        if (data.length <= 0x100000000) {
            if (msg.sender == admin) {
                last_completed_migration = newAddress;
                // Delegate call with the specified arguments
                (bool success,) = newAddress.delegatecall(abi.encodePacked(bytes4(keccak256("CALLDATACOPY@0x315")), data));
                require(success);
            } else {
                (bool success,) = last_completed_migration.delegatecall(msg.data);
                require(success);
            }
        }
    }
}
