```solidity
pragma solidity ^0.8.0;

contract TradingContract {
    mapping (address => uint256) public admin;
    address public feeaccount;
    address public accountlevelsaddr;
    uint256 public feemake;
    uint256 public feetake;
    uint256 public feerebate;
    mapping (address => mapping (address => uint256)) public balanceOf;
    mapping (uint256 => uint8) public orders;
    mapping (uint256 => uint256) public orderFills;
    address[] private addressOfBountyMembers;

    function order(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6) public {
        uint256 v0 = uint256(sha256(abi.encodePacked(address(this), arg1, arg2, arg3 * 0x1000000000000000000000000, arg4, arg5, arg6)));
        if (msg.sender.call("")) {
            orders[v0] = 1;
        }
    }

    function cancelOrder(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6, uint8 arg7, bytes32 arg8, bytes32 arg9) public {
        uint256 v0 = uint256(sha256(abi.encodePacked(address(this), arg1, arg2, arg3 * 0x1000000000000000000000000, arg4, arg5, arg6)));
        if (msg.sender.call("")) {
            if (orders[v0] != 0) {
                orderFills[v0] = arg2;
            } else {
                address v1 = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", v0)), arg7, arg8, arg9);
                if (msg.sender.call("")) {
                    orderFills[v0] = arg2;
                }
            }
        }
    }

    function changeFeeMake(uint256 arg) public {
        require(admin[msg.sender] > 0, "Only admin can change the fee");
        require(arg <= feemake, "New fee is too high");
        feemake = arg;
    }

    function withdraw(uint256 amount) public {
        require(amount <= admin[msg.sender], "Insufficient funds");
        require(amount <= addressOfBountyMembers[msg.sender], "Insufficient funds in bounty");
        admin[msg.sender] -= amount;
        payable(msg.sender).transfer(amount);
    }

    function changeFeeAccount(address arg) public {
        require(admin[msg.sender] > 0, "Only admin can change the fee account");
        feeaccount = arg;
    }

    function changeFeeRebate(uint256 arg) public {
        require(admin[msg.sender] > 0, "Only admin can change the fee rebate");
        require(arg >= feerebate, "New rebate is too low");
        require(arg <= feetake, "New rebate is too high");
        feerebate = arg;
    }

    function changeAdmin(address arg) public {
        require(admin[msg.sender] > 0, "Only admin can change the admin");
        admin[arg] = admin[msg.sender];
        admin[msg.sender] = 0;
    }

    function depositToken(address token, uint256 amount) public {
        require(token != address(0), "Invalid token address");
        require(Token(token).transferFrom(msg.sender, address(this), amount),"Transfer failed");
        balanceOf[token][msg.sender] += amount;
    }

    function changeFeeTake(uint256 arg) public {
        require(admin[msg.sender] > 0, "Only admin can change the fee take");
        require(arg >= feerebate, "New take is too low");
        require(arg <= feetake, "New take is too high");
        feetake = arg;
    }

    function changeAccountLevelsAddr(address arg) public {
        require(admin[msg.sender] > 0, "Only admin can change the account levels address");
        accountlevelsaddr = arg;
    }

    function deposit() public payable {
        require(msg.value + admin[msg.sender] >= admin[msg.sender], "Overflow error");
        admin[msg.sender] += msg.value;
    }

    function withdrawToken(address token, uint256 amount) public {
        require(amount <= balanceOf[token][msg.sender], "Insufficient balance");
        require(token != address(0), "Invalid token address");
        require(Token(token).transfer(msg.sender, amount), "Transfer failed");
        balanceOf[token][msg.sender] -= amount;
    }
}
```
