pragma solidity ^0.6.0;

contract YourContract {
    address public owner;
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    uint256 public sellprice;
    uint256 public buyprice;
    mapping (address => bool) public frozenaccount;
    uint256 public currentchallenge;
    uint256 public timeoflastproof;
    uint256 public difficulty;

    constructor() public payable {
        decimals = 18;
        difficulty = 0x4ee2d6d415b85acef8100000000;
        owner = msg.sender;
    }

    function setPrices(uint256 _sellprice, uint256 _buyprice) public {
        require(msg.sender == owner);
        sellprice = _sellprice;
        buyprice = _buyprice;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function burn(uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
        return true;
    }

    function mintToken(address _target, uint256 _mintedAmount) public {
        require(msg.sender == owner);
        balanceof[_target] += _mintedAmount;
        totalsupply += _mintedAmount;
    }

    // Proof of Work function and other functions are omitted for brevity

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner);
        owner = newOwner;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balanceof[msg.sender] >= _value);
        require(balanceof[_to] + _value > balanceof[_to]);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    // approveAndCall function and other functions are omitted for brevity
}
