pragma solidity ^0.8.0;

contract TokenContract {
    uint256 public totalsupply;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    bool public paused;
    address public owner;
    uint256 public onetokeninwei;
    uint256 public totalweirecieved;

    constructor() payable {
        paused = false;
        onetokeninwei = 0x27ca57357c000;
        if (msg.value == 0) {
            owner = msg.sender;
        }
    }

    function setEthPrice(uint256 price) public {
        require(msg.sender == owner);
        onetokeninwei = price;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function unpause() public {
        require(msg.sender == owner && paused);
        paused = false;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(allowance[_from][msg.sender] >= _value && balanceof[_from] >= _value && _value > 0);
        balanceof[_to] += _value;
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
    }

    function pause() public {
        require(msg.sender == owner && !paused);
        paused = true;
    }

    function generateTokens(address _to, uint256 _value) public {
        require(_to != address(0) && msg.sender == owner);
        balanceof[_to] += _value;
        totalsupply += _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(balanceof[msg.sender] >= _value && _value > 0);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0) && msg.sender == owner);
        owner = newOwner;
    }

    fallback() external payable {
        require((((0x5f5e100 * msg.value) / onetokeninwei) * 0x2540be400) + totalsupply <= 0x108b2a2c28029094000000 && !paused && msg.value > 0);
        balanceof[msg.sender] += (((0x5f5e100 * msg.value) / onetokeninwei) * 0x2540be400);
        totalsupply += (((0x5f5e100 * msg.value) / onetokeninwei) * 0x2540be400);
        totalweirecieved += msg.value;
    }

    function withdraw(address payable _to, uint256 _value) public {
        require(_to != address(0) && msg.sender == owner);
        _to.transfer(_value);
    }
}
```

This contract code is written in Solidity language version 0.8.0. It implements a basic ERC20 token contract with additional functionality such as pause, unpause, ownership transfer and ether withdrawal.
