Based on the provided description, here is an example of how the Smart Contract could be implemented in Solidity:

```Solidity
pragma solidity ^0.5.16;

contract TokenContract {
    address public owner;
    address private owner2;
    string public name;
    string public symbol;
    uint256 public totalsupply;
    uint256 public totalcontractbalance;
    uint256 public buyingprice;
    mapping (address => uint256) public balanceof;
    mapping (address => uint8) public checkgamelisted;
    mapping (address => uint8) public superreferrerrate;

    constructor() public payable {
        owner = msg.sender;
        owner2 = address(0);
        name = "0";
        symbol = "0";
        totalsupply = 0;
        totalcontractbalance = 0;
        buyingprice = 0x38d7ea4c68000;
    }

    function addSuperReferrer(address _referrer, uint8 _rate) external {
        require(msg.sender == owner, "Only owner can add super referrer.");
        superreferrerrate[_referrer] = _rate;
    }

    function changeName(string memory _name) public {
        require(msg.sender == owner, "Only owner can change name.");
        name = _name;
    }

    function changeNewHDX20Contract(address _newOwner) public {
        require(msg.sender == owner && owner2 != _newOwner, "Only owner can change HDX20 Contract owner.");
        owner2 = _newOwner;
    }

    function changeOwner(address _newOwner) public {
        require(msg.sender == owner && owner != _newOwner, "Only owner can change owner.");
        owner = _newOwner;
    }

    function removeGame(address _game) public {
        require(msg.sender == owner, "Only owner can remove game.");
        checkgamelisted[_game] = 0;
    }

    function changeSymbol(string memory _symbol) public {
        require(msg.sender == owner, "Only owner can change symbol.");
        symbol = _symbol;
    }

    function addGame(address _game) public {
        require(msg.sender == owner, "Only owner can add game.");
        checkgamelisted[_game] = 1;
    }

    function appreciateTokenPrice() public payable {
        require(msg.value + totalcontractbalance >= totalcontractbalance && checkgamelisted[msg.sender] == 1, "Value is not valid or game is not listed.");
        totalcontractbalance += msg.value;
        if (totalsupply != 0 && 0xde0b6b3a7640000 < totalsupply) {
            if (totalcontractbalance == 0) {
                buyingprice = 0;
            } else {
                buyingprice = (0xde0b6b3a7640000 * totalcontractbalance) / totalsupply;
            }
        }
    }

    // Complete the rest of the functions here based on your requirements
}
```

Note that this is a simplified example and doesn't cover all the conditions from your description. It's also important to apply best practices and security measures when you're writing smart contracts.
