Here is the contract code based on the given specifications:

```solidity
pragma solidity 0.6.12;

interface IERC20 {
    function transferFrom(address from, address to, uint256 value) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 value) external returns (bool);
}

contract YourContract {
    address payable public owner;
    address public updater;
    bool public paused;
    address payable public captain;
    address public po8token;
    uint256 public exchangerate;
    uint256 public cut;

    constructor() public {
        owner = msg.sender;
        paused = false;
    }

    function transferUpdater(address _updater) public {
        require(msg.sender == owner && _updater != address(0), "Not authorized");
        updater = _updater;
    }

    function unpause() public {
        require(msg.sender == owner && paused, "Not authorized or not paused");
        paused = false;
    }

    function setPO8TokenContractAdress(address _po8token) public {
        require(msg.sender == owner, "Not authorized");
        po8token = _po8token;
    }

    function withdrawBalance() public {
        require(msg.sender == owner, "Not authorized");
        owner.transfer(address(this).balance);
    }

    function getBackERC20Token(address _token) public {
        require(msg.sender == owner, "Not authorized");
        IERC20 token = IERC20(_token);
        uint256 balance = token.balanceOf(address(this));
        token.transfer(owner, balance);
    }

    function pause() public {
        require(msg.sender == owner && !paused, "Not authorized or already paused");
        paused = true;
    }

    function setCaptain(address payable _captain) public {
        require(msg.sender == owner && _captain != address(0), "Not authorized");
        captain = _captain;
    }

    function setExchangeRate(uint256 _exchangerate) public {
        require(msg.sender == updater, "Not authorized");
        exchangerate = _exchangerate;
    }

    function transferOwnership(address payable _newOwner) public {
        require(msg.sender == owner && _newOwner != address(0), "Not authorized");
        owner = _newOwner;
    }

    function buyPO8() public payable {
        require(msg.value >= 10000 && !paused, "Not authorized or paused");
        IERC20 token = IERC20(po8token);
        uint256 tokens = msg.value * exchangerate;
        uint256 commission = (msg.value * cut) / 10000;

        if (tokens > 0) {
            require(token.transferFrom(owner, msg.sender, tokens), "Transfer failed");
        }

        captain.transfer(commission);
    }
}
```
This is a basic implementation and does not include any error checking beyond the require statements. It also assumes that the PO8 token conforms to the standard ERC20 interface. For a production contract, you would want to add more checks and potentially events to help with transaction tracking.
