```solidity
pragma solidity ^0.5.16;

contract SampleContract {
    address private owner1;
    address private owner;
    address private owner2;
    uint256 public mindeposit;
    mapping(address => uint256) public holders;

    constructor() public payable {
        owner = msg.sender;
        owner2 = msg.sender;
    }

    function() external payable {
        if (msg.value >= mindeposit) {
            holders[msg.sender] += msg.value;
        }
    }

    function initTokenBank() public {
        owner2 = msg.sender;
        mindeposit = 0xde0b6b3a7640000;
    }

    function changeOwner(address newOwner) public {
        require(msg.sender == owner, "Only owner can change the ownership");
        owner1 = newOwner;
    }

    function confirmOwner() public {
        require(msg.sender == owner1, "Only proposed owner can confirm ownership");
        owner = owner1;
    }

    function Deposit() public payable {
        if (msg.value >= mindeposit) {
            holders[msg.sender] += msg.value;
        }
    }

    function WithdrawToken(address token, uint256 amount, address to) public {
        require(msg.sender == owner, "Only owner can withdraw tokens");
        IERC20(token).transfer(to, amount);
    }

    function WithdrawTokenToHolder(address holder, address token, uint256 amount) public {
        require(holders[holder] > 0 && msg.sender == owner, "Only owner can withdraw tokens to holder");
        holders[holder] = 0;
        IERC20(token).transfer(holder, amount);
    }

    function WithdrawToHolder(address holder, uint256 amount) public payable {
        require(holders[holder] >= amount && holders[msg.sender] > 0 && msg.sender == owner, "Only owner can withdraw to holder");
        holders[holder] -= amount;
    }
}

interface IERC20 {
    function transfer(address to, uint256 value) external returns (bool);
}
```

In the code above, we have a contract `SampleContract` with several functions as per your requirement. The IERC20 interface is used to interact with other ERC20 token contracts.
