pragma solidity >=0.4.22 <0.9.0;

contract Funding {
    address private owner;
    address private owner1;
    address private owner2;
    address private token;
    bool private paused;
    bool private paused1;
    bool private paused2;
    bool private paused3;
    bool public viewallcontrols;
    bool public withdrawfund;
    uint256 private totalSupply;
    uint256 private totalSupply1;
    uint256 private totalSupply2;
    uint256 private deadline;
    mapping(address => uint256) public balanceof;

    constructor() public {
        owner = msg.sender;
        owner1 = owner;
        viewallcontrols = false;
        withdrawfund = false;
        paused = false;
        paused1 = false;
        paused2 = false;
    }

    function setContrAddr(address _token, address _owner2) public {
        require(msg.sender == owner);
        token = _token;
        owner2 = _owner2;
    }

    function setFundingGoal(uint256 goal, bool controlSet) public {
        require(msg.sender == owner);
        if(!viewallcontrols && !controlSet) {
            totalSupply1 = goal * 0xde0b6b3a7640000;
            viewallcontrols = true;
        } else if(controlSet && viewallcontrols) {
            totalSupply1 = goal * 0xde0b6b3a7640000;
            viewallcontrols = true;
        }
    }

    function checkGoalReached() public {
        require(block.timestamp >= deadline);
        if(paused) {
            if(totalSupply >= totalSupply1) {
                withdrawfund = true;
                paused1 = true;
            } else if(totalSupply < totalSupply1) {
                withdrawfund = false;
            }
        }
    }

    function unlockFundrBal(bool unlock) public {
        require(block.timestamp >= deadline && msg.sender == owner);
        paused2 = unlock;
    }

    function transferOr(address newOwner) public {
        require(msg.sender == owner);
        owner = newOwner;
    }

    function endOfRewardsConfirmed(bool endConfirmed) public {
        require(paused3 && msg.sender == owner && endConfirmed);
        selfdestruct(owner);
    }

    // Additional functions are not included due to character limitations.
}
