Sure, here is a Solidity contract according to your specifications:

```solidity
pragma solidity ^0.8.0;

contract YourContract {
    address public owner;
    address public newowner;
    string public standard;
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;

    constructor() {
        owner = msg.sender;
        standard = "Your Standard";
        name = "Your Token";
        symbol = "YTK";
        decimals = 18;
        totalsupply = 0;
    }

    function approve(address _spender, uint256 _value) public {
        require(_spender != address(0));
        if(_value == 0 || allowance[msg.sender][_spender] == 0){
            allowance[msg.sender][_spender] = _value;
        }
    }

    fallback() external payable {
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
    }

    function acceptOwnership() public {
        require(msg.sender == newowner);
        owner = newowner;
        newowner = address(0);
    }

    function withdrawTo(address _to, uint256 _value) public {
        require(_to != address(this));
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
        payable(_to).transfer(_value);
    }

    function withdraw(uint256 _value) public {
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
        payable(msg.sender).transfer(_value);
    }

    function withdrawTokens(address _tokenContract, address _to, uint256 _value) public {
        require(msg.sender == owner && _to != address(0) && _tokenContract != address(0));
        ERC20 token = ERC20(_tokenContract);
        token.transfer(_to, _value);
    }

    function transferOwnership(address _newowner) public {
        require(msg.sender == owner && _newowner != owner);
        newowner = _newowner;
    }

    function deposit() public payable {
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_to != address(this) && _from != address(0) && _to != address(0));
        allowance[_from][msg.sender] -= _value;
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(this) && _to != address(0));
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }
}

interface ERC20 {
    function transfer(address _to, uint256 _value) external;
}
```

