Based on your contract description, here is the Solidity smart contract:

```solidity
pragma solidity ^0.6.2;

contract YourContract {
    uint256 public totalsupply;
    address public owner;
    address public transfer_proxy_vefx;
    address public transfer_proxy_v2;
    mapping(address => uint256) public issigner;
    string public name;
    string public symbol;
    uint256 public decimals;
    address public originaltoken;
    mapping(address => uint256) public depositlock;
    mapping(address => uint256) public balanceof;

    constructor() payable public {
        transfer_proxy_vefx = 0x7e03D2B8edc3585eCd8A5807661fff0830a0b603;
        transfer_proxy_v2 = 0x2240Dab907db71e64d3E0dbA4800c83B5C502d4E;
        originaltoken = address(0);
        owner = msg.sender;
    }

    function isValidSignature(bytes32 r, uint8 s, bytes32 v1, bytes32 v2) public view returns(bool) {
        return ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", r)), s, v1, v2) == msg.sender;
    }

    function withdraw(uint256 amount, uint8 v, bytes32 r, bytes32 s, uint256 blocknumber) public {
        require(balanceof[msg.sender] >= amount);
        balanceof[msg.sender] -= amount;
        totalsupply -= msg.value;
        msg.sender.transfer(amount);
        depositlock[msg.sender] = 0;
        if(blocknumber > block.number && depositlock[msg.sender] == 0) {
            address v0 = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", keccak256(abi.encodePacked(msg.sender, address(this)*0x1000000000000000000000000, blocknumber)))), v, r, s);
            if (issigner[v0] != 0) {
                balanceof[msg.sender] -= amount;
            }
        }
    }

    function transferFrom(address from, address to, uint256 amount) public {
        if(issigner[to] != 0) {
            balanceof[to] += amount;
            balanceof[from] -= amount;
            if(depositlock[to] > 0) {
                depositlock[to] = 3600 + now;
            }
        }
        if(issigner[from] != 0 && issigner[to] == 0) {
            balanceof[to] += amount;
            balanceof[from] -= amount;
            if(depositlock[to] > 0) {
                depositlock[to] = 3600 + now;
            }
        }
    }

    function addSigner(address newSigner) public {
        require(issigner[msg.sender] != 0);
        issigner[newSigner] = 1;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner && newOwner != address(0));
        owner = newOwner;
    }

    function deposit(uint256 amount, uint256 time) public payable {
        require(time >= 1 && ((time * 3600) + now) >= depositlock[msg.sender]);
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
        depositlock[msg.sender] = (time * 3600) + now;
    }

    function withdrawDifferentToken(address token, bool isCaller) public {
        require(msg.sender == owner);
        uint256 v2 = IERC20(token).balanceOf(address(this));
        require(v2 > 0);
        if(isCaller) {
            IERC20(token).transfer(msg.sender, v2);
        } else {
            IERC20(token).transfer(owner, v2);
        }
    }
}

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
}
```

In this contract, please replace the `YourContract` with the name of your contract. Also, please replace `IERC20` interface method calls with the actual method calls of your token contract.
