```solidity
pragma solidity ^0.5.0;

contract TokenContract {
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping (address => uint256)) public allowance;
    uint256 public totalSupply;
    address public owner;
    uint256 public price;
    uint256 public collectedTokens;
    uint256 public collectedEthers;
    uint256 public startTime;
    uint256 public weiRaised;
    uint256 public tokensSold;
    uint8 public isFinished;

    constructor() public payable {
        require(msg.value == 0);
        isFinished = 0;
        owner = msg.sender;
        totalSupply = 0x17d7840;
        price = 0xc6f3b40b6c000;
        startTime = 0x5d19d9a0;
    }

    function changePrice(uint256 _price) public {
        require(msg.sender == owner);
        price = _price;
    }

    function withdraw() public {
        require(msg.sender.send(collectedEthers));
        if (!msg.sender.send(collectedEthers)) {
            collectedEthers = 0;
        }
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function stop() public {
        require(msg.sender == owner);
        isFinished = 1;
    }

    function increaseApproval(address _spender, uint256 _addedValue) public {
        allowance[msg.sender][_spender] += _addedValue;
    }

    function decreaseApproval(address _spender, uint256 _subtractedValue) public {
        uint256 oldValue = allowance[msg.sender][_spender];
        if (_subtractedValue > oldValue) {
            allowance[msg.sender][_spender] = 0;
        } else {
            allowance[msg.sender][_spender] = oldValue - _subtractedValue;
        }
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0));
        owner = newOwner;
    }

    function burn(uint256 _value) public {
        require(_value <= balanceOf[msg.sender]);
        balanceOf[msg.sender] -= _value;
        totalSupply -= _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0));
        require(_value <= balanceOf[msg.sender]);
        require(balanceOf[_to] + _value >= balanceOf[_to]);
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function burnFrom(address _from, uint256 _value) public {
        require(_value <= allowance[_from][msg.sender]);
        allowance[_from][msg.sender] -= _value;
        require(_value <= balanceOf[_from]);
        balanceOf[_from] -= _value;
        totalSupply -= _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= balanceOf[_from]);
        require(_value <= allowance[_from][msg.sender]);
        require(_to != address(0));
        require(balanceOf[_to] + _value >= balanceOf[_to]);
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }
}
```

Please note that this is a simple contract and does not include any security checks or error handling that would be needed for a production system. In a real-world situation, you should also consider adding events to log important actions.
