pragma solidity ^0.5.0;

contract DepositContract {
    mapping (address => uint256) public showdeposit;
    mapping (address => uint256) public showlastchange;

    uint256 private price = 0x2386f26fc10000;
    address payable private owner = 0x6fDb012E4a57623eA74Cc1a6E5095Cda63f2C767;
    address payable private owner1 = 0xf62f85457f97CE475AAa5523C5739Aa8d4ba64C1;
    address payable private owner2 = 0xf62f85457f97CE475AAa5523C5739Aa8d4ba64C1;

    function() external payable {
        require(msg.value >= price, "Not enough Ether provided.");

        // Transfer funds to owners
        owner.transfer(msg.value / 10);
        owner1.transfer(msg.value / 100);
        owner2.transfer(msg.value / 100);

        // Calculate and transfer rewards
        uint256 reward;
        if (showdeposit[msg.sender] != 0) {
            reward = ((showdeposit[msg.sender] * 400 / 10000) * (block.number - showlastchange[msg.sender])) / 5900;
            msg.sender.transfer(reward);
        }
        if (showdeposit[msg.sender] >= 0x22b1c8c1227a00000) {
            reward = ((showdeposit[msg.sender] * 500 / 10000) * (block.number - showlastchange[msg.sender])) / 5900;
            msg.sender.transfer(reward);
        }
        if (showdeposit[msg.sender] >= 0xde0b6b3a7640000 && showdeposit[msg.sender] < 0x8ac7230489e80000) {
            reward = ((showdeposit[msg.sender] * 425 / 10000) * (block.number - showlastchange[msg.sender])) / 5900;
            msg.sender.transfer(reward);
        }
        if (showdeposit[msg.sender] >= 0x8ac7230489e80000 && showdeposit[msg.sender] < 0x1158e460913d00000) {
            reward = ((showdeposit[msg.sender] * 450 / 10000) * (block.number - showlastchange[msg.sender])) / 5900;
            msg.sender.transfer(reward);
        }
        if (showdeposit[msg.sender] >= 0x1158e460913d00000 && showdeposit[msg.sender] < 0x22b1c8c1227a00000) {
            reward = ((showdeposit[msg.sender] * 475 / 10000) * (block.number - showlastchange[msg.sender])) / 5900;
            msg.sender.transfer(reward);
        }

        // Update the deposit and last change
        showdeposit[msg.sender] += msg.value;
        showlastchange[msg.sender] = block.number;
    }
}
