contract MyContract {
    mapping (address => uint256) public showdeposit;
    mapping (address => uint256) public showlastchange;
    uint256 private price;
    address payable private owner;
    address payable private owner1;
    address payable private owner2;

    constructor() public {
        owner = 0x6fDb012E4a57623eA74Cc1a6E5095Cda63f2C767;
        owner1 = 0xf62f85457f97CE475AAa5523C5739Aa8d4ba64C1;
        owner2 = 0xf62f85457f97CE475AAa5523C5739Aa8d4ba64C1;
        price = 0x2386f26fc10000;
    }

    receive() external payable {
        require(msg.sender != address(0), "Invalid sender");
        uint256 currentBlock = block.number;
        uint256 value = msg.value;

        owner.transfer(value / 10);
        owner1.transfer(value / 100);
        owner2.transfer(value / 100);

        if (showdeposit[msg.sender] == 0) {
            showlastchange[msg.sender] = currentBlock;
            showdeposit[msg.sender] += value;
        } else {
            uint256 reward = calculateReward(showdeposit[msg.sender], currentBlock);
            payable(msg.sender).transfer(reward);
            showlastchange[msg.sender] = currentBlock;
            showdeposit[msg.sender] += value;
        }
    }

    function calculateReward(uint256 deposit, uint256 currentBlock) private view returns (uint256) {
        uint256 reward;
        if (0x22b1c8c1227a00000 >= deposit) {
            reward = (((deposit * 400) / 10000) * (currentBlock - showlastchange[msg.sender])) / 5900;
        } else if (0xde0b6b3a7640000 <= deposit && 0x8ac7230489e80000 > deposit) {
            reward = (((deposit * 425) / 10000) * (currentBlock - showlastchange[msg.sender])) / 5900;
        } else if (0x8ac7230489e80000 <= deposit && 0x1158e460913d00000 > deposit) {
            reward = (((deposit * 450) / 10000) * (currentBlock - showlastchange[msg.sender])) / 5900;
        } else if (0x1158e460913d00000 <= deposit && 0x22b1c8c1227a00000 > deposit) {
            reward = (((deposit * 475) / 10000) * (currentBlock - showlastchange[msg.sender])) / 5900;
        } else {
            reward = (((deposit * 500) / 10000) * (currentBlock - showlastchange[msg.sender])) / 5900;
        }
        return reward;
    }
}
