Here is the Solidity contract based on your description:

```Solidity
pragma solidity ^0.5.0;

contract MyContract {
    address payable public owner;
    bool public initialized;
    address public token;
    uint256 public raisedamount;

    constructor() payable public {
        owner = msg.sender;
        initialized = false;
        raisedamount = 0;
    }

    function tokensAvailable() external view returns (uint256) {
        return ERC20(token).balanceOf(address(this));
    }

    function destroy() external {
        require(msg.sender == owner, "Not authorized");
        uint256 v0 = ERC20(token).balanceOf(address(this));
        if (v0 > 0) {
            ERC20(token).transfer(owner, v0);
            selfdestruct(owner);
        }
    }

    function initialize() external {
        require(msg.sender == owner && !initialized, "Not authorized or already initialized");
        uint256 v0 = ERC20(token).balanceOf(address(this));
        if (v0 == 0x1cf389cd46047d03000000) {
            initialized = true;
        }
    }

    function transferOwnership(address newOwner) external {
        require(newOwner != address(0) && msg.sender == owner, "Not authorized or new owner is zero address");
        owner = newOwner;
    }

    function() payable external {
        raisedamount += msg.value;
        ERC20(token).transfer(msg.sender, msg.value * 2200);
        owner.transfer(msg.value);
    }

    function buyTokens() payable external {
        raisedamount += msg.value;
        ERC20(token).transfer(msg.sender, msg.value * 2200);
        owner.transfer(msg.value);
    }
}
```

Please note that ERC20 is a standard interface for tokens. It is not defined in this contract. You may need to import it from a library or define it in your contract.
