pragma solidity 0.6.12;

contract YourContract {
    uint256 public timeout;
    uint256 public kbalance;
    uint256 public feerate;
    address payable public technicalrise;
    address payable public lastbidder;

    constructor() payable public {
        technicalrise = 0x7c0Bf55bAb08B4C1eBac3FC115C394a739c62538;
        lastbidder = msg.sender;
        kbalance = msg.value;
        timeout = block.timestamp + 600;
        feerate = 100;
    }

    fallback() payable external {
        uint256 fee = msg.value/feerate;
        uint256 valueAfterFee = msg.value - fee;
        kbalance += valueAfterFee;

        (bool success, ) = technicalrise.call{value: fee}("");
        require(success, "Transfer failed.");

        if(valueAfterFee < 0x232bff5f46c000) {
            timeout += 120;
        } else {
            timeout += (0x526f1e874dd20000/valueAfterFee);
            lastbidder = msg.sender;
            if(timeout <= block.timestamp) {
                (bool success1, ) = lastbidder.call{value: (kbalance - valueAfterFee)}("");
                require(success1, "Transfer failed.");
                kbalance = valueAfterFee;
                lastbidder = msg.sender;
                timeout = block.timestamp + (0x526f1e874dd20000/valueAfterFee);
            }
        }
    }

    function fund() payable public {
        uint256 fee = msg.value/feerate;
        uint256 valueAfterFee = msg.value - fee;
        kbalance += valueAfterFee;

        (bool success, ) = technicalrise.call{value: fee}("");
        require(success, "Transfer failed.");

        if(valueAfterFee < 0x232bff5f46c000) {
            timeout += 120;
        } else {
            timeout += (0x526f1e874dd20000/valueAfterFee);
            lastbidder = msg.sender;
            if(timeout <= block.timestamp) {
                (bool success1, ) = lastbidder.call{value: (kbalance - valueAfterFee)}("");
                require(success1, "Transfer failed.");
                kbalance = valueAfterFee;
                lastbidder = msg.sender;
                timeout = block.timestamp + (0x526f1e874dd20000/valueAfterFee);
            }
        }
    }
}
```

