pragma solidity ^0.5.0;

contract YourContract {
    uint8 public decimals;
    uint256 public totalsupply;
    uint256 public totalsold;
    string public name;
    string public symbol;
    address public owner;
    address public finance;
    address public jackpot;
    address public sharecontract;

    mapping (address => uint256) private balanceOf2;
    mapping (address => uint256) private balanceOf3;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;

    uint256 private totalSupply2;

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    function approve(address _spender, uint256 _value) public {
        require(_value > 0 && allowance[msg.sender][_spender] == 0);
        allowance[msg.sender][_spender] = _value;
    }

    function() external payable {
        if (msg.value > 0) {
            totalSupply2 += msg.value / totalsupply;
        }
    }

    function setShare(address _sharecontract) public onlyOwner {
        sharecontract = _sharecontract;
    }

    function setJackpot(address _jackpot) public onlyOwner {
        jackpot = _jackpot;
    }

    function setFinance(address _finance) public onlyOwner {
        finance = _finance;
    }

    function increaseProfit() public payable {
        if (msg.value > 0) {
            totalSupply2 += msg.value / totalsupply;
        }
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= allowance[_from][msg.sender]);
        allowance[_from][msg.sender] -= _value;
        if (_value > 0 && _value <= balanceof[_from] && _to != _from && _to != address(0)) {
            balanceof[_to] += _value;
            balanceof[_from] -= _value;
            if (_value == 0) {
                balanceOf2[_from] = balanceOf2[_from];
                balanceOf3[_to] = balanceOf3[_to];
            } else {
                balanceOf2[_from] += totalSupply2 * _value;
                balanceOf3[_to] += totalSupply2 * _value;
            }
        }
    }

    // ... continue with the remaining functions as per your contract description ...
}
