```solidity
pragma solidity ^0.8.0;

contract YourContract {
    address private owner;
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => bool) public blacklist;
    uint256 public totalsupply;
    uint256 public totaldistributed;
    uint256 public totalremaining;
    uint256 public value;
    bool public distributionfinished;

    constructor() payable {
        owner = msg.sender;
        totalsupply = 0x916720a817a68ab4c8000000;
        totaldistributed = 0x88adbd6ace8d3076bc000000;
    }

    function approve(address _to, uint256 _value) public {
        if (_value == 0) {
            allowance[msg.sender][_to] = _value;
        } else if (_value != 0 && allowance[msg.sender][_to] == 0) {
            allowance[msg.sender][_to] = _value;
        }
    }

    function withdraw() public {
        require(msg.sender == owner);
        payable(owner).transfer(address(this).balance);
    }

    function finishDistribution() public {
        require(msg.sender == owner && !distributionfinished);
        distributionfinished = true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= allowance[_from][msg.sender]);
        require(_to != address(0));
        require(_value <= balanceOf[_from]);
        balanceOf[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function getTokenBalance(address _tokenContract, address _who) public view returns (uint256 balance) {
        ERC20 token = ERC20(_tokenContract);
        return token.balanceOf(_who);
    }

    function burn(uint256 _value) public {
        require(_value <= balanceOf[msg.sender] && msg.sender == owner);
        balanceOf[msg.sender] -= _value;
        totalsupply -= _value;
        totaldistributed -= _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0));
        require(_value <= balanceOf[msg.sender]);
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
    }

    function transferOwnership(address newOwner) public {
        require(newOwner != address(0));
        require(msg.sender == owner);
        owner = newOwner;
    }

    fallback() external payable {
        require(!distributionfinished && !blacklist[msg.sender]);
        if (msg.value <= totalremaining) {
            totaldistributed += msg.value;
            totalremaining -= msg.value;
            balanceOf[msg.sender] += msg.value;
            value = (msg.value / 0x186a0) * 0x1869f;
            if (msg.value > 0) {
                blacklist[msg.sender] = true;
            }
            if (totaldistributed >= totalsupply) {
                distributionfinished = true;
            }
        } else {
            value = totalremaining;
        }
    }

    function withdrawForeignTokens(address _tokenContract) public {
        require(msg.sender == owner);
        ERC20 token = ERC20(_tokenContract);
        uint256 received = token.balanceOf(address(this));
        token.transfer(owner, received);
    }

    function getTokens() public payable {
        require(!distributionfinished && !blacklist[msg.sender]);
        if (msg.value <= totalremaining) {
            totaldistributed += msg.value;
            totalremaining -= msg.value;
            balanceOf[msg.sender] += msg.value;
            value = (msg.value / 0x186a0) * 0x1869f;
            if (msg.value > 0) {
                blacklist[msg.sender] = true;
            }
            if (totaldistributed >= totalsupply) {
                distributionfinished = true;
            }
        } else {
            value = totalremaining;
        }
    }
}

interface ERC20 {
    function balanceOf(address who) external view returns (uint256);
    function transfer(address to, uint256 value) external returns (bool);
}
