```solidity
pragma solidity ^0.6.6;

contract MyContract {
    address public owner;
    string public name;
    string public symbol;
    uint8 public decimals = 5;
    uint256 public totalsupply;
    uint256 public sellprice;
    uint256 public buyprice;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => bool) public frozenaccount;

    constructor() public payable {
        owner = msg.sender;
    }

    function setPrices(uint256 newSellPrice, uint256 newBuyPrice) public {
        require(msg.sender == owner);
        sellprice = newSellPrice;
        buyprice = newBuyPrice;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    fallback() external payable {
        if (buyprice != 0 && balanceof[msg.sender] + msg.value / buyprice >= balanceof[msg.sender] && frozenaccount[msg.sender] == false && frozenaccount[address(this)] == false && balanceof[address(this)] >= msg.value / buyprice) {
            balanceof[address(this)] -= msg.value / buyprice;
            balanceof[msg.sender] += msg.value / buyprice;
        }
    }

    function burn(uint256 _value) public returns (bool success) {
        if (balanceof[msg.sender] >= _value) {
            balanceof[msg.sender] -= _value;
            totalsupply -= _value;
            return true;
        } else {
            return false;
        }
    }

    function withdrawEther(uint256 amount) public {
        if(msg.sender == owner) {
            msg.sender.transfer(amount);
        }
    }

    function mintToken(address target, uint256 mintedAmount) public {
        if(msg.sender == owner) {
            balanceof[target] += mintedAmount;
            totalsupply += mintedAmount;
        }
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        if (_value <= allowance[_from][msg.sender] && balanceof[_to] + _value >= balanceof[_to] && frozenaccount[_from] == false && frozenaccount[_to] == false && _to != address(0) && balanceof[_from] >= _value) {
            balanceof[_from] -= _value;
            balanceof[_to] += _value;
            allowance[_from][msg.sender] -= _value;
            return true;
        } else {
            return false;
        }
    }

    // Other functions go here.
}
```

Please note that this is a simple example and you will need to add event logs, error handling, and other functions as needed. This code also assumes that the contract does not comply with the ERC20 standard, which is the most commonly used standard for Ethereum tokens. If you want your contract to be an ERC20 token, you will need to add a few more functions and events.
